import React, { useState, useEffect } from 'react';

function LiveGrade3() {
  const [isLiveStreamActive, setIsLiveStreamActive] = useState(false);
  const [liveStreamUrl, setLiveStreamUrl] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchLiveStream = async () => {
      try {
        // ÿ•ÿ∂ÿßŸÅÿ© timestamp ŸÑÿ™ÿ¨ŸÜÿ® ÿßŸÑŸÉÿßÿ¥
        const res = await fetch(
          `https://raw.githubusercontent.com/omareldalil24/PIONEER/main/public/grades/grade3/live.json?t=${Date.now()}`
        );
        if (res.ok) {
          const data = await res.json();
          console.log('‚úÖ ÿ™ŸÖ ÿ¨ŸÑÿ® ÿ•ÿπÿØÿßÿØÿßÿ™ ÿßŸÑÿ®ÿ´ ŸÖŸÜ ÿßŸÑÿ£ÿØŸÖŸÜ:', data);
          setIsLiveStreamActive(data.isActive || false);
          
          // ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ ÿßŸÑÿ±ÿßÿ®ÿ∑ ŸÉŸÖÿß ŸáŸà ÿ®ÿØŸàŸÜ ÿ™ÿ≠ŸàŸäŸÑ
          setLiveStreamUrl(data.streamUrl || '');
          
          console.log('üîó ÿ±ÿßÿ®ÿ∑ ÿßŸÑÿ®ÿ´ ÿßŸÑŸÖÿ®ÿßÿ¥ÿ±:', data.streamUrl);
        } else {
          // ŸÑÿß ŸäŸàÿ¨ÿØ ÿ®ÿ´ ŸÖÿ®ÿßÿ¥ÿ±
          setIsLiveStreamActive(false);
          setLiveStreamUrl('');
        }
      } catch (error) {
        console.error('Error fetching live stream:', error);
        // ŸÑÿß ŸäŸàÿ¨ÿØ ÿ®ÿ´ ŸÖÿ®ÿßÿ¥ÿ± ŸÅŸä ÿ≠ÿßŸÑÿ© ÿßŸÑÿÆÿ∑ÿ£
        setIsLiveStreamActive(false);
        setLiveStreamUrl('');
      } finally {
        setLoading(false);
      }
    };
    
    fetchLiveStream();
    
    // ÿ™ÿ≠ÿØŸäÿ´ ÿ≠ÿßŸÑÿ© ÿßŸÑÿ®ÿ´ ŸÉŸÑ 10 ÿ´ŸàÿßŸÜŸê (ÿ£ÿ≥ÿ±ÿπ ŸÖŸÜ ŸÇÿ®ŸÑ)
    const interval = setInterval(fetchLiveStream, 10000);
    
    return () => clearInterval(interval);
  }, []);

  // ŸÅÿ™ÿ≠ ÿßŸÑÿ®ÿ´ ŸÅŸä ŸÜÿßŸÅÿ∞ÿ© ÿ¨ÿØŸäÿØÿ© ŸÖÿπ ÿ•ÿÆŸÅÿßÿ° ŸÖÿπŸÑŸàŸÖÿßÿ™ ÿßŸÑŸÇŸÜÿßÿ©
  const openLiveStream = () => {
    if (liveStreamUrl) {
      // ŸÅÿ™ÿ≠ ŸÅŸä ŸÜÿßŸÅÿ∞ÿ© ÿ¨ÿØŸäÿØÿ© ÿπÿßÿØŸäÿ©
      const newWindow = window.open(
        liveStreamUrl, 
        'liveBroadcast',
        'width=1200,height=800,scrollbars=yes,resizable=yes,status=no,location=no,toolbar=no,menubar=no'
      );
      
      if (newWindow) {
        newWindow.focus();
        console.log('‚úÖ ÿ™ŸÖ ŸÅÿ™ÿ≠ ÿßŸÑÿ®ÿ´ ŸÅŸä ŸÜÿßŸÅÿ∞ÿ© ÿ¨ÿØŸäÿØÿ©');
        
        // ŸÖÿ≠ÿßŸàŸÑÿ© ÿ•ÿÆŸÅÿßÿ° ŸÖÿπŸÑŸàŸÖÿßÿ™ ŸäŸàÿ™ŸäŸàÿ® (ŸÇÿØ ŸÑÿß ÿ™ÿπŸÖŸÑ ÿ®ÿ≥ÿ®ÿ® Same-Origin Policy)
        setTimeout(() => {
          try {
            const doc = newWindow.document;
            if (doc) {
              // ÿ•ÿÆŸÅÿßÿ° ÿπŸÜÿßÿµÿ± ŸäŸàÿ™ŸäŸàÿ®
              const style = doc.createElement('style');
              style.textContent = `
                .ytp-chrome-top, 
                .ytp-title,
                .ytp-chrome-top-buttons,
                .ytp-watermark,
                .ytp-youtube-button,
                .ytp-show-cards-title,
                .ytp-cards-button,
                .ytp-videowall-still,
                .ytp-ce-element,
                #movie_player .ytp-chrome-top,
                #movie_player .ytp-title,
                ytd-watch-next-secondary-results-renderer,
                ytd-compact-video-renderer,
                .ytd-watch-next-secondary-results-renderer,
                #secondary,
                #related {
                  display: none !important;
                  visibility: hidden !important;
                }
              `;
              doc.head.appendChild(style);
            }
          } catch (e) {
            console.log('ŸÑÿß ŸäŸÖŸÉŸÜ ÿ™ÿπÿØŸäŸÑ ŸÖÿ≠ÿ™ŸàŸâ ÿßŸÑŸÜÿßŸÅÿ∞ÿ© ÿßŸÑÿ¨ÿØŸäÿØÿ© ŸÑÿ£ÿ≥ÿ®ÿßÿ® ÿ£ŸÖŸÜŸäÿ©');
          }
        }, 2000);
      } else {
        // ÿ•ÿ∞ÿß ŸÅÿ¥ŸÑ ŸÅÿ™ÿ≠ ÿßŸÑŸÜÿßŸÅÿ∞ÿ© ÿßŸÑÿ¨ÿØŸäÿØÿ©ÿå ÿ¨ÿ±ÿ® ÿßŸÑÿ∑ÿ±ŸäŸÇÿ© ÿßŸÑÿπÿßÿØŸäÿ©
        window.open(liveStreamUrl, '_blank', 'noopener,noreferrer');
      }
    }
  };

  // ŸÅÿ™ÿ≠ ÿßŸÑÿ®ÿ´ ŸÅŸä ŸÜŸÅÿ≥ ÿßŸÑŸÜÿßŸÅÿ∞ÿ©
  const watchInCurrentWindow = () => {
    if (liveStreamUrl) {
      window.location.href = liveStreamUrl;
    }
  };

  // ŸÜÿ≥ÿÆ ÿßŸÑÿ±ÿßÿ®ÿ∑ ŸÖÿπ ÿ™ÿ£ÿ´Ÿäÿ± ÿ®ÿµÿ±Ÿä
  const [copied, setCopied] = useState(false);
  const copyLink = async () => {
    try {
      await navigator.clipboard.writeText(liveStreamUrl);
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    } catch (err) {
      // fallback ŸÑŸÑŸÖÿ™ÿµŸÅÿ≠ÿßÿ™ ÿßŸÑŸÇÿØŸäŸÖÿ©
      const textArea = document.createElement('textarea');
      textArea.value = liveStreamUrl;
      document.body.appendChild(textArea);
      textArea.select();
      document.execCommand('copy');
      document.body.removeChild(textArea);
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    }
  };

  if (loading) {
    return (
      <div style={{
        minHeight: '100vh',
        background: 'linear-gradient(to bottom right, #c31432, #8e0000, #240b36)',
        backgroundAttachment: 'fixed',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center'
      }}>
        <div style={{ textAlign: 'center', color: 'white' }}>
          <div style={{
            width: '60px',
            height: '60px',
            border: '4px solid rgba(255, 255, 255, 0.3)',
            borderTop: '4px solid #ffffff',
            borderRadius: '50%',
            animation: 'spin 1s linear infinite',
            margin: '0 auto 25px'
          }}></div>
          <h3 style={{ 
            fontWeight: '700', 
            fontSize: '1.8rem',
            textShadow: '0 2px 4px rgba(0, 0, 0, 0.3)'
          }}>üî¥ ÿ¨ÿßÿ±Ÿä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ÿ´ ÿßŸÑŸÖÿ®ÿßÿ¥ÿ±...</h3>
        </div>
      </div>
    );
  }

  return (
    <>
      <div
        style={{
          minHeight: '100vh',
          background: 'linear-gradient(to bottom right, #c31432, #8e0000, #240b36)',
          backgroundSize: 'cover',
          backgroundRepeat: 'no-repeat',
          backgroundAttachment: 'fixed',
          paddingTop: '100px',
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center'
        }}
      >
        {/* Header Section */}
        <div style={{
          background: 'rgba(255, 255, 255, 0.95)',
          backdropFilter: 'blur(20px)',
          margin: '0 auto 40px auto',
          padding: '30px 35px',
          borderRadius: '20px',
          maxWidth: '600px',
          width: '90%',
          textAlign: 'center',
          boxShadow: '0 15px 35px rgba(0, 0, 0, 0.1)',
          border: '1px solid rgba(255, 255, 255, 0.3)',
          animation: 'slideInUp 0.6s ease-out'
        }}>
          <div style={{ 
            fontSize: '3.5rem', 
            marginBottom: '15px',
            ...(isLiveStreamActive ? {animation: 'pulse 2s infinite'} : {})
          }}>üî¥</div>
          <h1 style={{ 
            fontSize: '2rem',
            fontWeight: '700',
            marginBottom: '15px',
            color: '#2c3e50'
          }}>
            ÿßŸÑÿ®ÿ´ ÿßŸÑŸÖÿ®ÿßÿ¥ÿ±
          </h1>
          <div style={{
            background: isLiveStreamActive ? 
              'linear-gradient(135deg, #28a745, #20c997)' : 
              'linear-gradient(135deg, #6c757d, #5a6268)',
            color: 'white',
            padding: '10px 20px',
            borderRadius: '20px',
            display: 'inline-block',
            fontSize: '0.9rem',
            fontWeight: '600',
            boxShadow: `0 4px 12px ${isLiveStreamActive ? 'rgba(40, 167, 69, 0.3)' : 'rgba(108, 117, 125, 0.3)'}`,
            position: 'relative'
          }}>
            {isLiveStreamActive && (
              <div style={{
                position: 'absolute',
                top: '-5px',
                right: '-5px',
                width: '12px',
                height: '12px',
                backgroundColor: '#ffffff',
                borderRadius: '50%',
                animation: 'pulse 1s infinite',
                boxShadow: '0 0 0 3px rgba(255, 255, 255, 0.3)'
              }}></div>
            )}
            {isLiveStreamActive ? 'üü¢ ÿßŸÑÿ®ÿ´ ŸÜÿ¥ÿ∑ ÿßŸÑÿ¢ŸÜ - ÿ™ÿ≠ÿØŸäÿ´ ŸÉŸÑ 10 ÿ´ŸàÿßŸÜŸê' : '‚è∏Ô∏è ŸÑÿß ŸäŸàÿ¨ÿØ ÿ®ÿ´ ÿ≠ÿßŸÑŸäÿßŸã'}
          </div>
        </div>

        {/* Content Container */}
        <div style={{
          width: '100%',
          maxWidth: '900px',
          padding: '0 20px 50px 20px',
          display: 'flex',
          justifyContent: 'center'
        }}>
          {/* ÿßŸÑŸÖÿ≠ÿ™ŸàŸâ ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿä */}
          {isLiveStreamActive && liveStreamUrl ? (
            <div className="live-content" style={{
              background: 'rgba(255, 255, 255, 0.1)',
              backdropFilter: 'blur(20px)',
              borderRadius: '25px',
              padding: '40px',
              border: '1px solid rgba(255, 255, 255, 0.2)',
              boxShadow: '0 8px 32px rgba(0, 0, 0, 0.2)',
              animation: 'slideInUp 0.8s ease-out',
              position: 'relative',
              maxWidth: '800px',
              width: '100%',
              textAlign: 'center'
            }}>
              {/* ŸÜŸÇÿßÿ∑ ÿßŸÑÿ®ÿ´ ÿßŸÑŸÖÿ®ÿßÿ¥ÿ± ÿßŸÑŸÖÿ™ÿπÿØÿØÿ© */}
              <div style={{
                position: 'absolute',
                top: '25px',
                right: '25px',
                width: '20px',
                height: '20px',
                backgroundColor: '#ff0000',
                borderRadius: '50%',
                animation: 'pulse 1.5s infinite',
                boxShadow: '0 0 0 8px rgba(255, 0, 0, 0.3)',
                zIndex: 10,
                border: '3px solid #ffffff'
              }}></div>

              <div style={{
                position: 'absolute',
                top: '25px',
                right: '55px',
                width: '15px',
                height: '15px',
                backgroundColor: '#ff4444',
                borderRadius: '50%',
                animation: 'pulse 1.8s infinite',
                boxShadow: '0 0 0 5px rgba(255, 68, 68, 0.2)',
                zIndex: 9,
                border: '2px solid #ffffff'
              }}></div>

              <div style={{
                position: 'absolute',
                top: '25px',
                right: '80px',
                width: '12px',
                height: '12px',
                backgroundColor: '#ff6666',
                borderRadius: '50%',
                animation: 'pulse 2.1s infinite',
                boxShadow: '0 0 0 3px rgba(255, 102, 102, 0.1)',
                zIndex: 8,
                border: '2px solid #ffffff'
              }}></div>

              {/* ÿπŸÑÿßŸÖÿ© LIVE ŸÖÿ≠ÿ≥ŸÜÿ© */}
              <div style={{
                position: 'absolute',
                top: '20px',
                left: '25px',
                background: 'linear-gradient(135deg, #ff0000, #cc0000)',
                color: 'white',
                padding: '8px 16px',
                borderRadius: '20px',
                fontSize: '0.9rem',
                fontWeight: '700',
                zIndex: 10,
                boxShadow: '0 4px 12px rgba(255, 0, 0, 0.4)',
                animation: 'pulse 2s infinite',
                border: '2px solid #ffffff',
                textShadow: '0 1px 2px rgba(0, 0, 0, 0.3)'
              }}>
                üî¥ LIVE NOW
              </div>

              {/* ÿ±ŸÖÿ≤ ÿßŸÑÿ®ÿ´ ÿßŸÑŸÉÿ®Ÿäÿ± ŸÖÿπ ÿ™ÿ£ÿ´Ÿäÿ± */}
              <div style={{
                fontSize: '6rem',
                marginBottom: '30px',
                color: 'white',
                textShadow: '0 4px 8px rgba(0, 0, 0, 0.3)',
                animation: 'pulse 3s infinite',
                filter: 'drop-shadow(0 0 20px rgba(255, 255, 255, 0.3))'
              }}>
                üì∫
              </div>

              {/* ÿπŸÜŸàÿßŸÜ ÿßŸÑÿ®ÿ´ */}
              <h2 style={{
                color: 'white',
                fontSize: '2.5rem',
                fontWeight: '700',
                marginBottom: '15px',
                textShadow: '0 2px 4px rgba(0, 0, 0, 0.3)',
                background: 'linear-gradient(135deg, #ffffff, #f8f9fa)',
                backgroundClip: 'text',
                WebkitBackgroundClip: 'text',
                WebkitTextFillColor: 'transparent'
              }}>
                ÿßŸÑÿ®ÿ´ ÿßŸÑŸÖÿ®ÿßÿ¥ÿ± ŸÜÿ¥ÿ∑ ÿßŸÑÿ¢ŸÜ! üöÄ
              </h2>

              <p style={{
                color: 'rgba(255, 255, 255, 0.9)',
                fontSize: '1.3rem',
                fontWeight: '500',
                marginBottom: '30px',
                lineHeight: '1.6'
              }}>
                <strong>ÿ∑ÿ±ŸÇ ÿßŸÑŸÖÿ¥ÿßŸáÿØÿ© ÿßŸÑŸÖÿ™ÿßÿ≠ÿ©!</strong> ÿßÿÆÿ™ÿ± ÿßŸÑÿ£ŸÜÿ≥ÿ® ŸÑŸÉ üëá
              </p>

              {/* ÿ£ÿ≤ÿßÿ± ÿßŸÑŸÖÿ¥ÿßŸáÿØÿ© ÿßŸÑŸÖÿ≠ÿ≥ŸÜÿ© - ÿ®ÿØŸàŸÜ ÿ™ÿ∑ÿ®ŸäŸÇ ŸäŸàÿ™ŸäŸàÿ® */}
              <div style={{
                display: 'grid',
                gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
                gap: '20px',
                maxWidth: '700px',
                margin: '0 auto 30px auto'
              }}>
                {/* ÿ≤ÿ± ŸÅÿ™ÿ≠ ŸÅŸä ŸÜÿßŸÅÿ∞ÿ© ÿ¨ÿØŸäÿØÿ© - ÿßŸÑÿ£ŸÅÿ∂ŸÑ */}
                <button
                  onClick={openLiveStream}
                  style={{
                    background: 'linear-gradient(135deg, #e74c3c, #c0392b)',
                    color: 'white',
                    border: '3px solid #ffffff',
                    borderRadius: '20px',
                    padding: '20px 25px',
                    fontSize: '1.2rem',
                    fontWeight: '700',
                    cursor: 'pointer',
                    transition: 'all 0.3s ease',
                    boxShadow: '0 10px 30px rgba(231, 76, 60, 0.4)',
                    textShadow: '0 2px 4px rgba(0, 0, 0, 0.2)',
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'center',
                    gap: '8px',
                    position: 'relative',
                    overflow: 'hidden'
                  }}
                  onMouseEnter={(e) => {
                    e.target.style.transform = 'translateY(-5px) scale(1.02)';
                    e.target.style.boxShadow = '0 15px 40px rgba(231, 76, 60, 0.5)';
                  }}
                  onMouseLeave={(e) => {
                    e.target.style.transform = 'translateY(0) scale(1)';
                    e.target.style.boxShadow = '0 10px 30px rgba(231, 76, 60, 0.4)';
                  }}
                >
                  <div style={{
                    position: 'absolute',
                    top: '10px',
                    right: '10px',
                    background: '#28a745',
                    color: 'white',
                    padding: '2px 8px',
                    borderRadius: '10px',
                    fontSize: '0.7rem',
                    fontWeight: '600'
                  }}>ÿßŸÑÿ£ŸÅÿ∂ŸÑ</div>
                  <span style={{ fontSize: '2rem' }}>üöÄ</span>
                  <div>ŸÖÿ¥ÿßŸáÿØÿ© ŸÅŸä ŸÜÿßŸÅÿ∞ÿ© ÿ¨ÿØŸäÿØÿ©</div>
                  <small style={{ opacity: 0.9, fontSize: '0.9rem' }}>Ÿäÿ≠ÿßŸÅÿ∏ ÿπŸÑŸâ ÿßŸÑŸÖŸàŸÇÿπ ŸÖŸÅÿ™Ÿàÿ≠</small>
                </button>

                {/* ÿ≤ÿ± ŸÅÿ™ÿ≠ ŸÅŸä ŸÜŸÅÿ≥ ÿßŸÑŸÜÿßŸÅÿ∞ÿ© */}
                <button
                  onClick={watchInCurrentWindow}
                  style={{
                    background: 'linear-gradient(135deg, #00b894, #00a085)',
                    color: 'white',
                    border: '3px solid #ffffff',
                    borderRadius: '20px',
                    padding: '20px 25px',
                    fontSize: '1.2rem',
                    fontWeight: '700',
                    cursor: 'pointer',
                    transition: 'all 0.3s ease',
                    boxShadow: '0 10px 30px rgba(0, 184, 148, 0.4)',
                    textShadow: '0 2px 4px rgba(0, 0, 0, 0.2)',
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'center',
                    gap: '8px'
                  }}
                  onMouseEnter={(e) => {
                    e.target.style.transform = 'translateY(-5px) scale(1.02)';
                    e.target.style.boxShadow = '0 15px 40px rgba(0, 184, 148, 0.5)';
                  }}
                  onMouseLeave={(e) => {
                    e.target.style.transform = 'translateY(0) scale(1)';
                    e.target.style.boxShadow = '0 10px 30px rgba(0, 184, 148, 0.4)';
                  }}
                >
                  <span style={{ fontSize: '2rem' }}>üì∫</span>
                  <div>ÿßŸÑÿßŸÜÿ™ŸÇÿßŸÑ ŸÑŸÑŸÖÿ¥ÿßŸáÿØÿ©</div>
                  <small style={{ opacity: 0.9, fontSize: '0.9rem' }}>ÿßŸÜÿ™ŸÇÿßŸÑ ŸÖÿ®ÿßÿ¥ÿ±</small>
                </button>
              </div>

              {/* ŸÜÿ≥ÿÆ ÿßŸÑÿ±ÿßÿ®ÿ∑ ŸÖÿ≠ÿ≥ŸÜ */}
              <div style={{
                background: 'rgba(255, 255, 255, 0.15)',
                borderRadius: '20px',
                padding: '25px',
                marginTop: '20px',
                border: '2px solid rgba(255, 255, 255, 0.2)'
              }}>
                <h4 style={{
                  color: 'white',
                  fontSize: '1.2rem',
                  fontWeight: '600',
                  marginBottom: '15px',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  gap: '10px'
                }}>
                  üîó ÿ¥ÿßÿ±ŸÉ ÿ±ÿßÿ®ÿ∑ ÿßŸÑÿ®ÿ´ ŸÖÿπ ÿ≤ŸÖŸÑÿßÿ¶ŸÉ
                </h4>
                <div style={{
                  display: 'flex',
                  gap: '12px',
                  alignItems: 'center',
                  flexWrap: 'wrap'
                }}>
                  <input
                    type="text"
                    value={liveStreamUrl}
                    readOnly
                    style={{
                      flex: 1,
                      padding: '15px 20px',
                      borderRadius: '15px',
                      border: '2px solid rgba(255, 255, 255, 0.3)',
                      background: 'rgba(255, 255, 255, 0.95)',
                      fontSize: '14px',
                      fontFamily: 'monospace',
                      color: '#2c3e50',
                      minWidth: '250px',
                      fontWeight: '500'
                    }}
                  />
                  <button
                    onClick={copyLink}
                    style={{
                      background: copied ? 
                        'linear-gradient(135deg, #28a745, #20c997)' : 
                        'linear-gradient(135deg, #f39c12, #e67e22)',
                      color: 'white',
                      border: '2px solid #ffffff',
                      borderRadius: '15px',
                      padding: '15px 25px',
                      fontSize: '14px',
                      fontWeight: '600',
                      cursor: 'pointer',
                      transition: 'all 0.3s ease',
                      whiteSpace: 'nowrap',
                      minWidth: '120px'
                    }}
                  >
                    {copied ? '‚úÖ ÿ™ŸÖ ÿßŸÑŸÜÿ≥ÿÆ!' : 'üìã ŸÜÿ≥ÿÆ ÿßŸÑÿ±ÿßÿ®ÿ∑'}
                  </button>
                </div>
                <small style={{
                  color: 'rgba(255, 255, 255, 0.8)',
                  fontSize: '13px',
                  display: 'block',
                  marginTop: '10px',
                  textAlign: 'center'
                }}>
                  üí° ÿßŸÜÿ≥ÿÆ ÿßŸÑÿ±ÿßÿ®ÿ∑ Ÿàÿ¥ÿßÿ±ŸÉŸá ŸÅŸä ÿßŸÑŸàÿßÿ™ÿ≥ÿßÿ® ÿ£Ÿà ŸÖÿπ ÿ£ÿµÿØŸÇÿßÿ¶ŸÉ
                </small>
              </div>

