// src/contents/grade3/VideosGrade3.js
import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Modal } from 'react-bootstrap';

function VideosGrade3() {
  const [videosData, setVideosData] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [selectedVideo, setSelectedVideo] = useState(null);
  const [loading, setLoading] = useState(true);
  const [updateNotification, setUpdateNotification] = useState('');

  useEffect(() => {
    const fetchVideos = async () => {
      try {
        const res = await fetch(
          'https://raw.githubusercontent.com/omareldalil24/PIONEER/main/public/grades/grade3/videos.json'
        );
        if (!res.ok) throw new Error('Failed to fetch videos');
        const data = await res.json();
        setVideosData(data);
      } catch (error) {
        console.error('Error fetching videos:', error);
        setVideosData([]);
      } finally {
        setLoading(false);
      }
    };
    fetchVideos();
  }, []);

  const handleOpenModal = (video) => {
    setSelectedVideo(video);
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
    setSelectedVideo(null);
  };

  // تحديد نوع العرض (live أو عادي)
  const isLiveVideo = (video) => {
    return video.isLive === true;
  };

  if (loading) {
    return (
      <div style={{
        minHeight: '100vh',
        background: 'linear-gradient(to bottom right, #c31432, #8e0000, #240b36)',
        backgroundAttachment: 'fixed',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center'
      }}>
        <div style={{ textAlign: 'center', color: 'white' }}>
          <div style={{
            width: '60px',
            height: '60px',
            border: '4px solid rgba(255, 255, 255, 0.3)',
            borderTop: '4px solid #ffffff',
            borderRadius: '50%',
            animation: 'spin 1s linear infinite',
            margin: '0 auto 25px'
          }}></div>
          <h3 style={{ 
            fontWeight: '700', 
            fontSize: '1.8rem',
            textShadow: '0 2px 4px rgba(0, 0, 0, 0.3)'
          }}>📹 جاري تحميل المحتوى...</h3>
        </div>
      </div>
    );
  }

  // تصفية الفيديوهات - Live أولاً
  const liveVideos = videosData.filter(video => isLiveVideo(video));
  const normalVideos = videosData.filter(video => !isLiveVideo(video));
  const sortedVideos = [...liveVideos, ...normalVideos];

  return (
    <>
      <div
        style={{
          minHeight: '100vh',
          background: 'linear-gradient(to bottom right, #c31432, #8e0000, #240b36)',
          backgroundSize: 'cover',
          backgroundRepeat: 'no-repeat',
          backgroundAttachment: 'fixed',
          paddingTop: '100px',
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center'
        }}
      >
        {/* Header Section - متوسط دائماً */}
        <div style={{
          background: 'rgba(255, 255, 255, 0.95)',
          backdropFilter: 'blur(20px)',
          margin: '0 auto 40px auto',
          padding: '30px 35px',
          borderRadius: '20px',
          maxWidth: '600px',
          width: '90%',
          textAlign: 'center',
          boxShadow: '0 15px 35px rgba(0, 0, 0, 0.1)',
          border: '1px solid rgba(255, 255, 255, 0.3)',
          animation: 'slideInUp 0.6s ease-out'
        }}>
          <div style={{ fontSize: '3.5rem', marginBottom: '15px' }}>
            {liveVideos.length > 0 ? '🔴' : '📹'}
          </div>
          <h1 style={{ 
            fontSize: '2rem',
            fontWeight: '700',
            marginBottom: '15px',
            color: '#2c3e50'
          }}>
            {liveVideos.length > 0 ? 'البث المباشر والفيديوهات' : 'فيديوهات المراجعة'}
          </h1>
          <p style={{ 
            fontSize: '1.1rem',
            fontWeight: '500',
            color: '#6c757d',
            marginBottom: '15px'
          }}>
            {liveVideos.length > 0 ? 'بث مباشر وتسجيلات شاملة للمنهج' : 'مراجعة شاملة ومبسطة للمنهج'}
          </p>
          {videosData.length > 0 && (
            <div style={{ display: 'flex', gap: '10px', justifyContent: 'center', flexWrap: 'wrap' }}>
              {liveVideos.length > 0 && (
                <div style={{
                  background: 'linear-gradient(135deg, #e74c3c, #c0392b)',
                  color: 'white',
                  padding: '10px 20px',
                  borderRadius: '20px',
                  fontSize: '0.9rem',
                  fontWeight: '600',
                  boxShadow: '0 4px 12px rgba(231, 76, 60, 0.3)',
                  position: 'relative',
                  display: 'flex',
                  alignItems: 'center',
                  gap: '8px'
                }}>
                  <div style={{
                    width: '8px',
                    height: '8px',
                    backgroundColor: '#ffffff',
                    borderRadius: '50%',
                    animation: 'pulse 1.5s infinite'
                  }}></div>
                  🔴 {liveVideos.length} بث مباشر
                </div>
              )}
              {normalVideos.length > 0 && (
                <div style={{
                  background: 'linear-gradient(135deg, #c31432, #8e0000)',
                  color: 'white',
                  padding: '10px 20px',
                  borderRadius: '20px',
                  fontSize: '0.9rem',
                  fontWeight: '600',
                  boxShadow: '0 4px 12px rgba(195, 20, 50, 0.3)'
                }}>
                  📹 {normalVideos.length} فيديو
                </div>
              )}
            </div>
          )}
        </div>

        {/* Content Container - متوسط للشاشات العادية */}
        <div style={{
          width: '100%',
          maxWidth: '1400px',
          padding: '0 20px 50px 20px',
          display: 'flex',
          justifyContent: 'center'
        }}>
          {/* إشعار التحديث */}
          {updateNotification && (
            <div style={{
              position: 'fixed',
              top: '120px',
              right: '20px',
              background: 'linear-gradient(135deg, #00b894, #00a085)',
              color: 'white',
              padding: '15px 25px',
              borderRadius: '20px',
              fontSize: '1rem',
              fontWeight: '600',
              zIndex: 1000,
              boxShadow: '0 8px 25px rgba(0, 184, 148, 0.4)',
              animation: 'slideInRight 0.5s ease-out'
            }}>
              {updateNotification}
            </div>
          )}

          {sortedVideos.length > 0 ? (
            <div className="videos-grid" style={{
              display: 'grid',
              gridTemplateColumns: 'repeat(3, 1fr)',
              gap: '40px',
              justifyItems: 'center',
              justifyContent: 'center',
              width: '100%'
            }}>
              {sortedVideos.map((video, index) => (
                <div
                  key={index}
                  onClick={() => handleOpenModal(video)}
                  className="video-item"
                  style={{
                    cursor: 'pointer',
                    transition: 'all 0.4s cubic-bezier(0.175, 0.885, 0.32, 1.275)',
                    animation: `slideInUp 0.6s ease-out ${index * 0.1}s both`,
                    position: 'relative',
                    borderRadius: '25px',
                    overflow: 'hidden'
                  }}
                  onMouseEnter={(e) => {
                    e.currentTarget.style.transform = 'translateY(-15px) scale(1.05)';
                    e.currentTarget.style.filter = 'brightness(1.1)';
                  }}
                  onMouseLeave={(e) => {
                    e.currentTarget.style.transform = 'translateY(0) scale(1)';
                    e.currentTarget.style.filter = 'brightness(1)';
                  }}
                >
                  {/* عناصر Live فقط للفيديوهات اللي isLive = true */}
                  {isLiveVideo(video) && (
                    <>
                      {/* نقطة البث المباشر */}
                      <div style={{
                        position: 'absolute',
                        top: '25px',
                        right: '25px',
                        width: '20px',
                        height: '20px',
                        backgroundColor: '#e74c3c',
                        borderRadius: '50%',
                        animation: 'pulse 1.5s infinite',
                        boxShadow: '0 0 0 8px rgba(231, 76, 60, 0.3)',
                        zIndex: 10,
                        border: '3px solid white'
                      }}></div>
                      
                      {/* علامة LIVE */}
                      <div style={{
                        position: 'absolute',
                        top: '20px',
                        left: '20px',
                        background: 'linear-gradient(135deg, #e74c3c, #c0392b)',
                        color: 'white',
                        padding: '6px 12px',
                        borderRadius: '15px',
                        fontSize: '12px',
                        fontWeight: '700',
                        zIndex: 10,
                        boxShadow: '0 4px 15px rgba(231, 76, 60, 0.4)',
                        textTransform: 'uppercase',
                        letterSpacing: '1px'
                      }}>
                        🔴 LIVE
                      </div>
                    </>
                  )}

                  <img 
                    src={video.thumbnail} 
                    alt={isLiveVideo(video) ? "بث مباشر" : "فيديو المراجعة"}
                    style={{
                      width: '100%',
                      maxWidth: '380px',
                      height: 'auto',
                      borderRadius: '25px',
                      boxShadow: '0 20px 40px rgba(0, 0, 0, 0.15)',
                      transition: 'all 0.4s ease',
                      border: `3px solid ${isLiveVideo(video) ? 'rgba(231, 76, 60, 0.8)' : 'rgba(195, 20, 50, 0.8)'}`
                    }}
                    onError={(e) => {
                      e.target.style.display = 'block';
                      const gradientColor = isLiveVideo(video) ? 
                        'linear-gradient(135deg, #e74c3c, #c0392b)' : 
                        'linear-gradient(135deg, #c31432, #8e0000)';
                      const text = isLiveVideo(video) ? 'LIVE BROADCAST' : 'فيديوهات المراجعة';
                      e.target.src = `data:image/svg+xml;base64,${btoa(`<svg width="380" height="285" viewBox="0 0 380 285" fill="none" xmlns="http://www.w3.org/2000/svg"><rect width="380" height="285" fill="url(#gradient)" rx="25"/><defs><linearGradient id="gradient" x1="0%" y1="0%" x2="100%" y2="100%"><stop offset="0%" style="stop-color:${isLiveVideo(video) ? '#e74c3c' : '#c31432'}"/><stop offset="100%" style="stop-color:${isLiveVideo(video) ? '#c0392b' : '#8e0000'}"/></linearGradient></defs><text x="190" y="155" font-family="Arial" font-size="22" fill="white" text-anchor="middle">${text}</text></svg>`)}`;
                    }}
                  />
                </div>
              ))}
            </div>
          ) : (
            <div style={{
              background: 'rgba(255, 255, 255, 0.1)',
              backdropFilter: 'blur(20px)',
              borderRadius: '25px',
              padding: '50px',
              textAlign: 'center',
              border: '1px solid rgba(255, 255, 255, 0.2)',
              boxShadow: '0 8px 32px rgba(0, 0, 0, 0.2)',
              maxWidth: '600px',
              width: '100%'
            }}>
              <div style={{ fontSize: '5rem', marginBottom: '25px', opacity: 0.7 }}>📹</div>
              <h3 style={{ 
                color: 'white', 
                marginBottom: '18px',
                fontSize: '1.8rem',
                fontWeight: '700',
                textShadow: '0 2px 4px rgba(0, 0, 0, 0.3)'
              }}>
                لا توجد فيديوهات حالياً
              </h3>
              <p style={{ 
                color: 'rgba(255, 255, 255, 0.8)',
                fontSize: '1.2rem',
                fontWeight: '500'
              }}>
                سيتم إضافة الفيديوهات والبث المباشر قريباً
              </p>
            </div>
          )}
        </div>

        {/* مودال الفيديو المحسن - منع التحكم الكامل */}
        <Modal 
          show={showModal} 
          onHide={handleCloseModal} 
          centered 
          size="lg"
          style={{ direction: 'rtl' }}
        >
          <Modal.Header 
            style={{
              background: selectedVideo && isLiveVideo(selectedVideo) ? 
                'linear-gradient(135deg, #e74c3c, #c0392b)' : 
                'linear-gradient(135deg, #c31432, #8e0000)',
              backdropFilter: 'blur(20px)',
              borderBottom: 'none',
              borderRadius: '15px 15px 0 0',
              padding: '20px 25px',
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
              direction: 'rtl',
              color: 'white'
            }}
          >
            <Modal.Title style={{
              fontSize: '1.3rem',
              fontWeight: '700',
              display: 'flex',
              alignItems: 'center',
              gap: '10px',
              margin: 0
            }}>
              {selectedVideo && isLiveVideo(selectedVideo) ? (
                <>
                  {/* نقطة البث المباشر في العنوان */}
                  <div style={{
                    width: '12px',
                    height: '12px',
                    backgroundColor: '#ffffff',
                    borderRadius: '50%',
                    animation: 'pulse 1.5s infinite',
                    marginLeft: '8px'
                  }}></div>
                  🔴 LIVE • {selectedVideo?.notes || 'بث مباشر'}
                </>
              ) : (
                <>
                  🎬 {selectedVideo?.notes || 'فيديو المراجعة'}
                </>
              )}
            </Modal.Title>
            <button
              onClick={handleCloseModal}
              style={{
                background: 'rgba(255, 255, 255, 0.2)',
                border: '2px solid rgba(255, 255, 255, 0.3)',
                borderRadius: '50%',
                width: '40px',
                height: '40px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                cursor: 'pointer',
                transition: 'all 0.3s ease',
                color: 'white',
                fontSize: '18px',
                fontWeight: '700'
              }}
              onMouseEnter={(e) => {
                e.target.style.background = 'rgba(255, 255, 255, 0.3)';
                e.target.style.transform = 'scale(1.1)';
              }}
              onMouseLeave={(e) => {
                e.target.style.background = 'rgba(255, 255, 255, 0.2)';
                e.target.style.transform = 'scale(1)';
              }}
            >
              ✕
            </button>
          </Modal.Header>
          <Modal.Body 
            style={{ 
              padding: '0',
              background: 'rgba(255, 255, 255, 0.95)',
              backdropFilter: 'blur(20px)',
              borderRadius: '0 0 15px 15px',
              position: 'relative'
            }}
          >
            {selectedVideo && (
              <div 
                id="video-container"
                style={{ 
                  position: 'relative', 
                  paddingBottom: '56.25%', 
                  height: 0, 
                  overflow: 'hidden',
                  background: '#000'
                }}
              >
                {/* الفيديو مع إعدادات منع التحكم الكامل */}
                <iframe
                  id="main-video-iframe"
                  title={selectedVideo && isLiveVideo(selectedVideo) ? "Live Stream" : "Video Preview"}
                  src={`https://drive.google.com/file/d/${selectedVideo.id}/preview`}
                  frameBorder="0"
                  allowFullScreen
                  style={{
                    position: 'absolute',
                    top: 0,
                    left: 0,
                    width: '100%',
                    height: '100%',
                    border: 'none',
                    pointerEvents: 'auto'
                  }}
                />

                {/* طبقة منع التحكم الكاملة - تغطي جميع أدوات التحكم */}
                <div 
                  className="video-controls-blocker"
                  style={{
                    position: 'absolute',
                    top: 0,
                    left: 0,
                    right: 0,
                    bottom: 0,
                    zIndex: 100,
                    pointerEvents: 'none',
                    userSelect: 'none',
                    background: 'transparent'
                  }}
                >
                  {/* منع التحكم في الجزء السفلي (شريط التحكم) */}
                  <div 
                    className="bottom-controls-block"
                    style={{
                      position: 'absolute',
                      bottom: 0,
                      left: 0,
                      right: 0,
                      height: '80px',
                      background: selectedVideo && isLiveVideo(selectedVideo) ? 
                        'linear-gradient(135deg, #e74c3c, #c0392b)' : 
                        'linear-gradient(135deg, #c31432, #8e0000)',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      color: 'white',
                      fontSize: '16px',
                      fontWeight: '700',
                      pointerEvents: 'auto',
                      userSelect: 'none',
                      zIndex: 150
                    }}
                    onMouseDown={(e) => e.preventDefault()}
                    onTouchStart={(e) => e.preventDefault()}
                    onClick={(e) => {
                      e.preventDefault();
                      e.stopPropagation();
                      return false;
                    }}
                  >
                    {selectedVideo && isLiveVideo(selectedVideo) ? (
                      <>
                        <div style={{
                          width: '10px',
                          height: '10px',
                          backgroundColor: '#ffffff',
                          borderRadius: '50%',
                          animation: 'pulse 1.5s infinite',
                          marginLeft: '10px'
                        }}></div>
                        🔴 LIVE STREAM • البث المباشر
                      </>
                    ) : (
                      '🎬 مشاهدة فقط • لا يمكن التحكم في الفيديو'
                    )}
                  </div>

                  {/* منع التحكم في الجزء العلوي (أيقونات Drive) */}
                  <div 
                    className="top-controls-block"
                    style={{
                      position: 'absolute',
                      top: 0,
                      right: 0,
                      width: '120px',
                      height: '80px',
                      background: selectedVideo && isLiveVideo(selectedVideo) ? 
                        'linear-gradient(135deg, #e74c3c, #c0392b)' : 
                        'linear-gradient(135deg, #c31432, #8e0000)',
                      borderRadius: '0 0 0 20px',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      color: 'white',
                      fontSize: '12px',
                      fontWeight: '700',
                      pointerEvents: 'auto',
                      userSelect: 'none',
                      zIndex: 150,
                      boxShadow: '0 4px 15px rgba(0, 0, 0, 0.4)'
                    }}
                    onMouseDown={(e) => e.preventDefault()}
                    onTouchStart={(e) => e.preventDefault()}
                    onClick={(e) => {
                      e.preventDefault();
                      e.stopPropagation();
                      return false;
                    }}
                  >
                    {selectedVideo && isLiveVideo(selectedVideo) ? '🔴 LIVE' : '🎬 VIDEO'}
                  </div>

                  {/* طبقة شفافة لمنع جميع التفاعلات مع الفيديو */}
                  <div 
                    className="interaction-blocker"
                    style={{
                      position: 'absolute',
                      top: '80px',
                      left: 0,
                      right: 0,
                      bottom: '80px',
                      background: 'transparent',
                      pointerEvents: 'auto',
                      userSelect: 'none',
                      zIndex: 120,
                      cursor: 'default'
                    }}
                    onMouseDown={(e) => e.preventDefault()}
                    onTouchStart={(e) => e.preventDefault()}
                    onContextMenu={(e) => e.preventDefault()}
                    onClick={(e) => {
                      e.preventDefault();
                      e.stopPropagation();
                      return false;
                    }}
                    onDoubleClick={(e) => {
                      e.preventDefault();
                      e.stopPropagation();
                      return false;
                    }}
                  />

                  {/* منع استخدام لوحة المفاتيح */}
                  <div 
                    className="keyboard-blocker"
                    style={{
                      position: 'absolute',
                      top: 0,
                      left: 0,
                      right: 0,
                      bottom: 0,
                      background: 'transparent',
                      pointerEvents: 'auto',
                      userSelect: 'none',
                      zIndex: 110
                    }}
                    onKeyDown={(e) => {
                      e.preventDefault();
                      e.stopPropagation();
                      return false;
                    }}
                    onKeyUp={(e) => {
                      e.preventDefault();
                      e.stopPropagation();
                      return false;
                    }}
                    tabIndex={-1}
                  />
                </div>

                {/* رسالة تحذيرية عند محاولة التفاعل */}
                <div 
                  className="warning-message"
                  style={{
                    position: 'absolute',
                    top: '50%',
                    left: '50%',
                    transform: 'translate(-50%, -50%)',
                    background: 'rgba(0, 0, 0, 0.8)',
                    color: 'white',
                    padding: '15px 25px',
                    borderRadius: '10px',
                    fontSize: '14px',
                    fontWeight: '600',
                    zIndex: 200,
                    opacity: 0,
                    transition: 'opacity 0.3s ease',
                    pointerEvents: 'none',
                    textAlign: 'center',
                    whiteSpace: 'nowrap'
                  }}
                  id="warning-msg"
                >
                  🚫 مشاهدة فقط - لا يمكن التحكم في الفيديو
                </div>
              </div>
            )}
          </Modal.Body>
        </Modal>
      </div>

      <style>
        {`
          @keyframes spin {
            0% { transform: rotate(0deg); }
            100% { transform: rotate(360deg); }
          }
          
          @keyframes pulse {
            0%, 100% { 
              opacity: 1; 
              transform: scale(1);
            }
            50% { 
              opacity: 0.7; 
              transform: scale(1.2);
            }
          }
          
          @keyframes slideInUp {
            from {
              opacity: 0;
              transform: translateY(30px);
            }
            to {
              opacity: 1;
              transform: translateY(0);
            }
          }
          
          @keyframes slideInRight {
            from { 
              opacity: 0; 
              transform: translateX(30px); 
            }
            to { 
              opacity: 1; 
              transform: translateX(0); 
            }
          }

          /* منع جميع أشكال التفاعل مع الفيديو */
          .video-controls-blocker {
            pointer-events: none !important;
            user-select: none !important;
            -webkit-user-select: none !important;
            -moz-user-select: none !important;
            -ms-user-select: none !important;
          }

          .bottom-controls-block,
          .top-controls-block {
            pointer-events: auto !important;
            user-select: none !important;
            -webkit-user-select: none !important;
            -moz-user-select: none !important;
            -ms-user-select: none !important;
          }

          .interaction-blocker {
            pointer-events: auto !important;
            user-select: none !important;
            -webkit-user-select: none !important;
            -moz-user-select: none !important;
            -ms-user-select: none !important;
            cursor: not-allowed !important;
          }

          /* منع استخدام اختصارات لوحة المفاتيح */
          #main-video-iframe {
            outline: none !important;
          }

          .keyboard-blocker {
            pointer-events: auto !important;
            outline: none !important;
          }

          .keyboard-blocker:focus {
            outline: none !important;
          }

          /* منع النقر المزدوج والنقر بالزر الأيمن */
          .interaction-blocker {
            -webkit-touch-callout: none !important;
            -webkit-user-select: none !important;
            -khtml-user-select: none !important;
            -moz-user-select: none !important;
            -ms-user-select: none !important;
            user-select: none !important;
          }

          /* منع السحب والإفلات */
          .video-controls-blocker * {
            -webkit-user-drag: none !important;
            -khtml-user-drag: none !important;
            -moz-user-drag: none !important;
            -o-user-drag: none !important;
            user-drag: none !important;
            draggable: false !important;
          }

          /* تحسين وضع fullscreen مع منع التحكم */
          .modal:-webkit-full-screen .bottom-controls-block,
          .modal:-moz-full-screen .bottom-controls-block,
          .modal:fullscreen .bottom-controls-block {
            position: fixed !important;
            bottom: 0 !important;
            left: 0 !important;
            right: 0 !important;
            height: 100px !important;
            z-index: 999999 !important;
            font-size: 20px !important;
          }

          .modal:-webkit-full-screen .top-controls-block,
          .modal:-moz-full-screen .top-controls-block,
          .modal:fullscreen .top-controls-block {
            position: fixed !important;
            top: 0 !important;
            right: 0 !important;
            width: 150px !important;
            height: 100px !important;
            z-index: 999999 !important;
            font-size: 16px !important;
          }

          .modal:-webkit-full-screen .interaction-blocker,
.modal:-moz-full-screen .interaction-blocker,
.modal:fullscreen .interaction-blocker {
 position: fixed !important;
 top: 100px !important;
 left: 0 !important;
 right: 0 !important;
 bottom: 100px !important;
 z-index: 999998 !important;
}

/* إظهار رسالة التحذير عند محاولة التفاعل */
.interaction-blocker:hover + .warning-message,
.interaction-blocker:active + .warning-message {
 opacity: 1 !important;
}

/* منع جميع الأحداث */
.video-controls-blocker,
.interaction-blocker,
.keyboard-blocker {
 -webkit-touch-callout: none !important;
 -webkit-user-select: none !important;
 -khtml-user-select: none !important;
 -moz-user-select: none !important;
 -ms-user-select: none !important;
 user-select: none !important;
 -webkit-tap-highlight-color: transparent !important;
}

/* منع استخدام F11 و Escape */
body {
 -webkit-user-select: none;
 -moz-user-select: none;
 -ms-user-select: none;
 user-select: none;
}

