 .video-item {
   width: 100% !important;
   max-width: 450px !important;
   margin: 0 auto !important;
 }
 
 .video-item img {
   max-width: 100% !important;
   width: 100% !important;
 }
}

/* تحسين التركيز للوصولية */
.video-item:focus {
 outline: 3px solid rgba(231, 76, 60, 0.5);
 outline-offset: 2px;
}

/* تحسين للحركة المنخفضة */
@media (prefers-reduced-motion: reduce) {
 * {
   animation-duration: 0.01ms !important;
   animation-iteration-count: 1 !important;
   transition-duration: 0.01ms !important;
 }
}

/* تحسين المودال للموبايل */
@media (max-width: 768px) {
 .modal-content {
   margin: 5px !important;
   border-radius: 15px !important;
 }
 
 .modal-header {
   padding: 15px 20px !important;
 }
 
 .modal-title {
   font-size: 1.1rem !important;
 }
}

/* منع النسخ واللصق */
.video-controls-blocker * {
 -webkit-user-select: none !important;
 -moz-user-select: none !important;
 -ms-user-select: none !important;
 user-select: none !important;
 -webkit-touch-callout: none !important;
 -webkit-tap-highlight-color: transparent !important;
}

/* منع استخدام أدوات المطور */
.video-controls-blocker {
 -webkit-user-select: none !important;
 -moz-user-select: none !important;
 -ms-user-select: none !important;
 user-select: none !important;
 pointer-events: none !important;
}

.bottom-controls-block,
.top-controls-block,
.interaction-blocker {
 pointer-events: auto !important;
 -webkit-user-select: none !important;
 -moz-user-select: none !important;
 -ms-user-select: none !important;
 user-select: none !important;
}

/* إخفاء الـ scrollbar الأفقي */
body {
 overflow-x: hidden;
}

/* تحسين أداء الانيميشن */
.video-item,
.video-item img {
 will-change: transform;
 transform: translateZ(0);
}

/* تحسين للشاشات العريضة جداً */
@media (min-width: 1600px) {
 .videos-grid {
   max-width: 1500px !important;
   grid-template-columns: repeat(4, 1fr) !important;
   gap: 50px !important;
 }
 
 .video-item img {
   max-width: 350px !important;
 }
}

@media (min-width: 1800px) {
 .videos-grid {
   max-width: 1600px !important;
   grid-template-columns: repeat(4, 1fr) !important;
   gap: 60px !important;
 }
 
 .video-item img {
   max-width: 380px !important;
 }
}

/* منع التفاعل مع الفيديو بشكل نهائي */
#main-video-iframe {
 pointer-events: auto !important;
 -webkit-user-select: none !important;
 -moz-user-select: none !important;
 user-select: none !important;
}

/* حماية شاملة من جميع أشكال التفاعل */
.video-controls-blocker {
 isolation: isolate !important;
 contain: layout style paint !important;
}

.interaction-blocker {
 contain: layout style paint !important;
 isolation: isolate !important;
}

/* منع استخدام shortcuts */
.modal-body {
 -webkit-user-select: none !important;
 -moz-user-select: none !important;
 user-select: none !important;
}

/* تأكيد نهائي على منع التحكم */
.bottom-controls-block,
.top-controls-block {
 position: absolute !important;
 background: linear-gradient(135deg, #c31432, #8e0000) !important;
 z-index: 150 !important;
 pointer-events: auto !important;
 user-select: none !important;
 -webkit-user-select: none !important;
 -moz-user-select: none !important;
 -ms-user-select: none !important;
}

.live-video .bottom-controls-block,
.live-video .top-controls-block {
 background: linear-gradient(135deg, #e74c3c, #c0392b) !important;
}

/* منع جميع أنواع events */
.interaction-blocker {
 -webkit-touch-callout: none !important;
 -webkit-user-select: none !important;
 -khtml-user-select: none !important;
 -moz-user-select: none !important;
 -ms-user-select: none !important;
 user-select: none !important;
 -webkit-tap-highlight-color: transparent !important;
 touch-action: none !important;
}

/* تحسين نهائي */
* {
 box-sizing: border-box;
}

.video-container {
 will-change: transform;
 transform: translateZ(0);
}

/* تأكيد عدم إمكانية التحكم */
.warning-message {
 font-family: 'Arial', sans-serif !important;
 text-shadow: 0 2px 4px rgba(0, 0, 0, 0.5) !important;
 border: 2px solid rgba(255, 255, 255, 0.3) !important;
}
