// إضافة JavaScript لمنع التحكم الكامل
window.addEventListener('DOMContentLoaded', function() {
 // منع استخدام اختصارات لوحة المفاتيح
 document.addEventListener('keydown', function(e) {
   const modal = document.querySelector('.modal.show');
   if (modal) {
     // منع جميع اختصارات التحكم في الفيديو
     const blockedKeys = [
       'Space', 'ArrowLeft', 'ArrowRight', 'ArrowUp', 'ArrowDown',
       'KeyK', 'KeyJ', 'KeyL', 'KeyF', 'KeyM', 'KeyC', 'Escape',
       'Digit0', 'Digit1', 'Digit2', 'Digit3', 'Digit4', 'Digit5',
       'Digit6', 'Digit7', 'Digit8', 'Digit9', 'Period', 'Comma'
     ];
     
     if (blockedKeys.includes(e.code) || 
         (e.ctrlKey && (e.key === 'a' || e.key === 'c' || e.key === 'v')) ||
         e.key === 'F11' || e.key === 'F12') {
       e.preventDefault();
       e.stopPropagation();
       e.stopImmediatePropagation();
       
       // إظهار رسالة التحذير
       const warningMsg = document.getElementById('warning-msg');
       if (warningMsg) {
         warningMsg.style.opacity = '1';
         setTimeout(() => {
           warningMsg.style.opacity = '0';
         }, 2000);
       }
       
       return false;
     }
   }
 }, true);

 // منع النقر بالزر الأيمن
 document.addEventListener('contextmenu', function(e) {
   const modal = document.querySelector('.modal.show');
   if (modal) {
     e.preventDefault();
     e.stopPropagation();
     return false;
   }
 }, true);

 // منع السحب والإفلات
 document.addEventListener('dragstart', function(e) {
   const modal = document.querySelector('.modal.show');
   if (modal) {
     e.preventDefault();
     e.stopPropagation();
     return false;
   }
 }, true);

 // منع النسخ
 document.addEventListener('copy', function(e) {
   const modal = document.querySelector('.modal.show');
   if (modal) {
     e.preventDefault();
     e.stopPropagation();
     return false;
   }
 }, true);

 // إضافة event listeners للتفاعل مع طبقات منع التحكم
 document.addEventListener('click', function(e) {
   if (e.target.classList.contains('interaction-blocker') || 
       e.target.classList.contains('bottom-controls-block') ||
       e.target.classList.contains('top-controls-block')) {
     e.preventDefault();
     e.stopPropagation();
     
     // إظهار رسالة التحذير
     const warningMsg = document.getElementById('warning-msg');
     if (warningMsg) {
       warningMsg.style.opacity = '1';
       setTimeout(() => {
         warningMsg.style.opacity = '0';
       }, 2000);
     }
     
     return false;
   }
 }, true);
});

