// src/components/AdminContentManagement.js
import React, { useState, useEffect } from 'react';

function AdminContentManagement() {
  const [activeTab, setActiveTab] = useState('videos');
  const [videos, setVideos] = useState([]);
  const [files, setFiles] = useState([]);
  const [liveSettings, setLiveSettings] = useState({ isActive: false, streamUrl: '' });
  const [loading, setLoading] = useState(false);
  
  // ŸÜŸÖŸàÿ∞ÿ¨ ÿ•ÿ∂ÿßŸÅÿ© ŸÅŸäÿØŸäŸà/ŸÖŸÑŸÅ ÿ¨ÿØŸäÿØ
  const [showAddModal, setShowAddModal] = useState(false);
  const [newItem, setNewItem] = useState({ 
    id: '', 
    thumbnail: '', 
    notes: '', 
    isLive: false // ÿÆŸäÿßÿ± ÿ¨ÿØŸäÿØ ŸÑŸÑÿßŸäŸÅ
  });
  const [editingItem, setEditingItem] = useState(null);

  // ÿ•ÿπÿØÿßÿØÿßÿ™ GitHub
  const GITHUB_TOKEN = process.env.REACT_APP_GITHUB_PAT;
  const REPO_OWNER = 'omareldalil24';
  const REPO_NAME = 'PIONEER';

  // ÿ¨ŸÑÿ® ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿπŸÜÿØ ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿµŸÅÿ≠ÿ©
  useEffect(() => {
    loadAllData();
  }, []);

  const loadAllData = async () => {
    setLoading(true);
    try {
      await Promise.all([loadVideos(), loadFiles(), loadLiveSettings()]);
    } catch (error) {
      console.error('ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™:', error);
    } finally {
      setLoading(false);
    }
  };

  // ÿ¨ŸÑÿ® ÿßŸÑŸÅŸäÿØŸäŸàŸáÿßÿ™
  const loadVideos = async () => {
    try {
      const response = await fetch(`https://raw.githubusercontent.com/${REPO_OWNER}/${REPO_NAME}/main/public/grades/grade3/videos.json?${Date.now()}`);
      if (response.ok) {
        const data = await response.json();
        setVideos(data || []);
      }
    } catch (error) {
      console.error('ÿÆÿ∑ÿ£ ŸÅŸä ÿ¨ŸÑÿ® ÿßŸÑŸÅŸäÿØŸäŸàŸáÿßÿ™:', error);
      setVideos([]);
    }
  };

  // ÿ¨ŸÑÿ® ÿßŸÑŸÖŸÑŸÅÿßÿ™
  const loadFiles = async () => {
    try {
      const response = await fetch(`https://raw.githubusercontent.com/${REPO_OWNER}/${REPO_NAME}/main/public/grades/grade3/files.json?${Date.now()}`);
      if (response.ok) {
        const data = await response.json();
        setFiles(data || []);
      }
    } catch (error) {
      console.error('ÿÆÿ∑ÿ£ ŸÅŸä ÿ¨ŸÑÿ® ÿßŸÑŸÖŸÑŸÅÿßÿ™:', error);
      setFiles([]);
    }
  };

  // ÿ¨ŸÑÿ® ÿ•ÿπÿØÿßÿØÿßÿ™ ÿßŸÑÿ®ÿ´ ÿßŸÑŸÖÿ®ÿßÿ¥ÿ±
  const loadLiveSettings = async () => {
    try {
      const response = await fetch(`https://raw.githubusercontent.com/${REPO_OWNER}/${REPO_NAME}/main/public/grades/grade3/live.json?${Date.now()}`);
      if (response.ok) {
        const data = await response.json();
        setLiveSettings(data || { isActive: false, streamUrl: '' });
      }
    } catch (error) {
      console.error('ÿÆÿ∑ÿ£ ŸÅŸä ÿ¨ŸÑÿ® ÿ•ÿπÿØÿßÿØÿßÿ™ ÿßŸÑÿ®ÿ´:', error);
      setLiveSettings({ isActive: false, streamUrl: '' });
    }
  };

  // ÿ≠ŸÅÿ∏ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ŸÅŸä GitHub
  const saveToGitHub = async (filePath, data, message) => {
    try {
      if (!GITHUB_TOKEN) {
        throw new Error('GITHUB_TOKEN ÿ∫Ÿäÿ± ŸÖŸàÿ¨ŸàÿØ');
      }

      let sha = null;
      try {
        const fileResponse = await fetch(`https://api.github.com/repos/${REPO_OWNER}/${REPO_NAME}/contents/${filePath}`, {
          headers: { 'Authorization': `Bearer ${GITHUB_TOKEN}` }
        });
        if (fileResponse.ok) {
          const fileData = await fileResponse.json();
          sha = fileData.sha;
        }
      } catch (error) {
        console.log('ÿßŸÑŸÖŸÑŸÅ ÿ∫Ÿäÿ± ŸÖŸàÿ¨ŸàÿØÿå ÿ≥Ÿäÿ™ŸÖ ÿ•ŸÜÿ¥ÿßÿ° ŸÖŸÑŸÅ ÿ¨ÿØŸäÿØ');
      }

      const jsonContent = JSON.stringify(data, null, 2);
      const base64Content = btoa(unescape(encodeURIComponent(jsonContent)));

      const requestBody = {
        message: message,
        content: base64Content,
        branch: 'main'
      };

      if (sha) {
        requestBody.sha = sha;
      }

      const response = await fetch(`https://api.github.com/repos/${REPO_OWNER}/${REPO_NAME}/contents/${filePath}`, {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${GITHUB_TOKEN}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(requestBody)
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(`ŸÅÿ¥ŸÑ ŸÅŸä ÿ≠ŸÅÿ∏ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™: ${errorData.message}`);
      }

      return true;
    } catch (error) {
      console.error('ÿÆÿ∑ÿ£ ŸÅŸä ÿ≠ŸÅÿ∏ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™:', error);
      throw error;
    }
  };

  // ÿ≠ŸÅÿ∏ ÿßŸÑŸÅŸäÿØŸäŸàŸáÿßÿ™
  const saveVideos = async (videosData) => {
    await saveToGitHub('public/grades/grade3/videos.json', videosData, 'ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑŸÅŸäÿØŸäŸàŸáÿßÿ™');
  };

  // ÿ≠ŸÅÿ∏ ÿßŸÑŸÖŸÑŸÅÿßÿ™
  const saveFiles = async (filesData) => {
    await saveToGitHub('public/grades/grade3/files.json', filesData, 'ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑŸÖŸÑŸÅÿßÿ™');
  };

  // ÿ≠ŸÅÿ∏ ÿ•ÿπÿØÿßÿØÿßÿ™ ÿßŸÑÿ®ÿ´ ÿßŸÑŸÖÿ®ÿßÿ¥ÿ±
  const saveLiveSettings = async (liveData) => {
    await saveToGitHub('public/grades/grade3/live.json', liveData, 'ÿ™ÿ≠ÿØŸäÿ´ ÿ•ÿπÿØÿßÿØÿßÿ™ ÿßŸÑÿ®ÿ´ ÿßŸÑŸÖÿ®ÿßÿ¥ÿ±');
  };

  // ÿ•ÿ∂ÿßŸÅÿ© ÿπŸÜÿµÿ± ÿ¨ÿØŸäÿØ
  const handleAddItem = async () => {
