// src/contents/grade3/LiveGrade3.js
import React, { useState, useEffect } from 'react';
import { Container } from 'react-bootstrap';

function LiveGrade3() {
  const [isLiveStreamActive, setIsLiveStreamActive] = useState(false);
  const [liveStreamUrl, setLiveStreamUrl] = useState('');
  const [loading, setLoading] = useState(true);

  // ÿØÿßŸÑÿ© ŸÑÿ™ÿ≠ŸàŸäŸÑ ÿ±ÿßÿ®ÿ∑ ŸäŸàÿ™ŸäŸàÿ® ÿ•ŸÑŸâ ÿ±ÿßÿ®ÿ∑ embed
  const convertYouTubeURL = (url) => {
    if (!url) return '';
    
    // ŸÖÿπÿßŸÑÿ¨ÿ© ÿ±Ÿàÿßÿ®ÿ∑ ŸäŸàÿ™ŸäŸàÿ® ÿßŸÑŸÖÿÆÿ™ŸÑŸÅÿ©
    let videoId = '';
    
    // ÿ±ÿßÿ®ÿ∑ ÿßŸÑÿ®ÿ´ ÿßŸÑŸÖÿ®ÿßÿ¥ÿ±: https://www.youtube.com/live/qK52qqYaS3o?feature=shared
    if (url.includes('youtube.com/live/')) {
      const match = url.match(/youtube\.com\/live\/([a-zA-Z0-9_-]+)/);
      if (match) videoId = match[1];
    }
    // ÿ±ÿßÿ®ÿ∑ ÿπÿßÿØŸä: https://www.youtube.com/watch?v=VIDEO_ID
    else if (url.includes('youtube.com/watch?v=')) {
      const match = url.match(/watch\?v=([a-zA-Z0-9_-]+)/);
      if (match) videoId = match[1];
    }
    // ÿ±ÿßÿ®ÿ∑ ŸÖÿÆÿ™ÿµÿ±: https://youtu.be/VIDEO_ID
    else if (url.includes('youtu.be/')) {
      const match = url.match(/youtu\.be\/([a-zA-Z0-9_-]+)/);
      if (match) videoId = match[1];
    }
    // ÿ±ÿßÿ®ÿ∑ embed ŸÖÿ®ÿßÿ¥ÿ±
    else if (url.includes('youtube.com/embed/')) {
      return url;
    }
    
    if (videoId) {
      return `https://www.youtube.com/embed/${videoId}?autoplay=1&mute=0&controls=1&showinfo=1&rel=0&modestbranding=1`;
    }
    
    return url;
  };

  useEffect(() => {
    const fetchLiveStream = async () => {
      try {
        // ÿ•ÿ∂ÿßŸÅÿ© timestamp ŸÑÿ™ÿ¨ŸÜÿ® ÿßŸÑŸÉÿßÿ¥
        const res = await fetch(
          `https://raw.githubusercontent.com/omareldalil24/PIONEER/main/public/grades/grade3/live.json?t=${Date.now()}`
        );
        if (res.ok) {
          const data = await res.json();
          console.log('‚úÖ ÿ™ŸÖ ÿ¨ŸÑÿ® ÿ•ÿπÿØÿßÿØÿßÿ™ ÿßŸÑÿ®ÿ´ ŸÖŸÜ ÿßŸÑÿ£ÿØŸÖŸÜ:', data);
          setIsLiveStreamActive(data.isActive || false);
          
          // ÿ™ÿ≠ŸàŸäŸÑ ÿßŸÑÿ±ÿßÿ®ÿ∑ ÿ•ÿ∞ÿß ŸÉÿßŸÜ ŸäŸàÿ™ŸäŸàÿ®
          const convertedUrl = convertYouTubeURL(data.streamUrl || '');
          setLiveStreamUrl(convertedUrl);
          
          console.log('üîó ÿßŸÑÿ±ÿßÿ®ÿ∑ ÿßŸÑÿ£ÿµŸÑŸä:', data.streamUrl);
          console.log('üîó ÿßŸÑÿ±ÿßÿ®ÿ∑ ÿßŸÑŸÖÿ≠ŸàŸÑ:', convertedUrl);
        } else {
          // ŸÑÿß ŸäŸàÿ¨ÿØ ÿ®ÿ´ ŸÖÿ®ÿßÿ¥ÿ±
          setIsLiveStreamActive(false);
          setLiveStreamUrl('');
        }
      } catch (error) {
        console.error('Error fetching live stream:', error);
        // ŸÑÿß ŸäŸàÿ¨ÿØ ÿ®ÿ´ ŸÖÿ®ÿßÿ¥ÿ± ŸÅŸä ÿ≠ÿßŸÑÿ© ÿßŸÑÿÆÿ∑ÿ£
        setIsLiveStreamActive(false);
        setLiveStreamUrl('');
      } finally {
        setLoading(false);
      }
    };
    
    fetchLiveStream();
    
    // ÿ™ÿ≠ÿØŸäÿ´ ÿ≠ÿßŸÑÿ© ÿßŸÑÿ®ÿ´ ŸÉŸÑ 15 ÿ´ÿßŸÜŸäÿ© (ÿ£ÿ≥ÿ±ÿπ ŸÖŸÜ ÿßŸÑŸÅŸäÿØŸäŸàŸáÿßÿ™ ŸàÿßŸÑŸÖŸÑŸÅÿßÿ™)
    const interval = setInterval(fetchLiveStream, 15000);
    
    return () => clearInterval(interval);
  }, []);

  if (loading) {
    return (
      <div style={{
        minHeight: '100vh',
        background: 'linear-gradient(to bottom right, #c31432, #8e0000, #240b36)',
        backgroundAttachment: 'fixed',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center'
      }}>
        <div style={{ textAlign: 'center', color: 'white' }}>
          <div style={{
            width: '60px',
            height: '60px',
            border: '4px solid rgba(255, 255, 255, 0.3)',
            borderTop: '4px solid #ffffff',
            borderRadius: '50%',
            animation: 'spin 1s linear infinite',
            margin: '0 auto 25px'
          }}></div>
          <h3 style={{ 
            fontWeight: '700', 
            fontSize: '1.8rem',
            textShadow: '0 2px 4px rgba(0, 0, 0, 0.3)'
          }}>üî¥ ÿ¨ÿßÿ±Ÿä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ÿ´ ÿßŸÑŸÖÿ®ÿßÿ¥ÿ±...</h3>
        </div>
      </div>
    );
  }

  return (
    <>
      <div
        style={{
          minHeight: '100vh',
          background: 'linear-gradient(to bottom right, #c31432, #8e0000, #240b36)',
          backgroundSize: 'cover',
          backgroundRepeat: 'no-repeat',
          backgroundAttachment: 'fixed',
          paddingTop: '100px',
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center'
        }}
      >
        {/* Header Section - ŸÖÿ™Ÿàÿ≥ÿ∑ ÿØÿßÿ¶ŸÖÿßŸã */}
        <div style={{
          background: 'rgba(255, 255, 255, 0.95)',
          backdropFilter: 'blur(20px)',
          margin: '0 auto 40px auto',
          padding: '30px 35px',
          borderRadius: '20px',
          maxWidth: '600px',
          width: '90%',
          textAlign: 'center',
          boxShadow: '0 15px 35px rgba(0, 0, 0, 0.1)',
          border: '1px solid rgba(255, 255, 255, 0.3)',
          animation: 'slideInUp 0.6s ease-out'
        }}>
          <div style={{ fontSize: '3.5rem', marginBottom: '15px' }}>üî¥</div>
          <h1 style={{ 
            fontSize: '2rem',
            fontWeight: '700',
            marginBottom: '15px',
            color: '#2c3e50'
          }}>
            ÿßŸÑÿ®ÿ´ ÿßŸÑŸÖÿ®ÿßÿ¥ÿ±
          </h1>
          <div style={{
            background: isLiveStreamActive ? 
              'linear-gradient(135deg, #28a745, #20c997)' : 
              'linear-gradient(135deg, #6c757d, #5a6268)',
            color: 'white',
            padding: '10px 20px',
            borderRadius: '20px',
            display: 'inline-block',
            fontSize: '0.9rem',
            fontWeight: '600',
            boxShadow: `0 4px 12px ${isLiveStreamActive ? 'rgba(40, 167, 69, 0.3)' : 'rgba(108, 117, 125, 0.3)'}`
          }}>
            {isLiveStreamActive ? 'üü¢ ÿßŸÑÿ®ÿ´ ŸÜÿ¥ÿ∑ ÿßŸÑÿ¢ŸÜ' : '‚è∏Ô∏è ŸÑÿß ŸäŸàÿ¨ÿØ ÿ®ÿ´ ÿ≠ÿßŸÑŸäÿßŸã'}
          </div>
        </div>

        {/* Content Container - ŸÖÿ™Ÿàÿ≥ÿ∑ ŸÑŸÑÿ¥ÿßÿ¥ÿßÿ™ ÿßŸÑÿπÿßÿØŸäÿ© */}
        <div style={{
          width: '100%',
          maxWidth: '900px', // ÿπÿ±ÿ∂ ŸÖÿ≠ÿØŸàÿØ ŸÑŸÑÿ¥ÿßÿ¥ÿßÿ™ ÿßŸÑÿπÿßÿØŸäÿ©
          padding: '0 20px 50px 20px',
          display: 'flex',
          justifyContent: 'center'
        }}>
          {/* ÿßŸÑŸÖÿ≠ÿ™ŸàŸâ ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿä */}
          {isLiveStreamActive && liveStreamUrl ? (
            <div className="live-content" style={{
              background: 'rgba(255, 255, 255, 0.1)',
              backdropFilter: 'blur(20px)',
              borderRadius: '25px',
              padding: '25px',
              border: '1px solid rgba(255, 255, 255, 0.2)',
              boxShadow: '0 8px 32px rgba(0, 0, 0, 0.2)',
              animation: 'slideInUp 0.8s ease-out',
              position: 'relative',
              maxWidth: '800px',
              width: '100%'
            }}>
              {/* ŸÜŸÇÿ∑ÿ© ÿßŸÑÿ®ÿ´ ÿßŸÑŸÖÿ®ÿßÿ¥ÿ± */}
              <div style={{
                position: 'absolute',
                top: '25px',
                right: '25px',
                width: '20px',
                height: '20px',
                backgroundColor: '#ff0000',
                borderRadius: '50%',
                animation: 'pulse 1.5s infinite',
                boxShadow: '0 0 0 8px rgba(255, 0, 0, 0.3)',
                zIndex: 10,
                border: '3px solid #ffffff'
              }}></div>

              {/* ÿπŸÑÿßŸÖÿ© LIVE */}
              <div style={{
                position: 'absolute',
                top: '20px',
                left: '25px',
                background: '#ff0000',
                color: 'white',
                padding: '5px 12px',
                borderRadius: '15px',
                fontSize: '0.8rem',
                fontWeight: '700',
                zIndex: 10,
                boxShadow: '0 2px 8px rgba(255, 0, 0, 0.3)',
                animation: 'pulse 2s infinite'
              }}>
                üî¥ LIVE
              </div>

              {/* ÿßŸÑŸÅŸäÿØŸäŸà */}
              <div style={{
                position: 'relative',
                paddingBottom: '56.25%', // ŸÜÿ≥ÿ®ÿ© 16:9
                height: 0,
                borderRadius: '20px',
                overflow: 'hidden',
                border: '3px solid rgba(255, 255, 255, 0.2)',
                boxShadow: '0 10px 30px rgba(0, 0, 0, 0.3)'
              }}>
                <iframe
                  src={liveStreamUrl}
                  title="ÿßŸÑÿ®ÿ´ ÿßŸÑŸÖÿ®ÿßÿ¥ÿ± - ŸäŸàÿ™ŸäŸàÿ®"
                  frameBorder="0"
                  allowFullScreen
                  allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share"
                  style={{
                    position: 'absolute',
                    top: 0,
                    left: 0,
                    width: '100%',
                    height: '100%'
                  }}
                />
              </div>

              {/* ŸÖÿπŸÑŸàŸÖÿßÿ™ ÿ•ÿ∂ÿßŸÅŸäÿ© */}
              <div style={{
                marginTop: '20px',
                textAlign: 'center'
              }}>
                <p style={{
                  color: 'rgba(255, 255, 255, 0.9)',
                  fontSize: '1rem',
                  fontWeight: '500',
                  margin: 0,
                  textShadow: '0 1px 2px rgba(0, 0, 0, 0.3)'
                }}>
                  üì∫ ŸäŸÖŸÉŸÜŸÉ ÿ™ŸÉÿ®Ÿäÿ± ÿßŸÑÿ¥ÿßÿ¥ÿ© ŸÑŸÑÿ≠ÿµŸàŸÑ ÿπŸÑŸâ ÿ™ÿ¨ÿ±ÿ®ÿ© ÿ£ŸÅÿ∂ŸÑ
                </p>
              </div>
            </div>
          ) : (
            /* ÿ≠ÿßŸÑÿ© ÿπÿØŸÖ Ÿàÿ¨ŸàÿØ ÿ®ÿ´ ŸÖÿ®ÿßÿ¥ÿ± */
            <div className="empty-state" style={{
              background: 'rgba(255, 255, 255, 0.1)',
              backdropFilter: 'blur(20px)',
              borderRadius: '25px',
              padding: '50px',
              textAlign: 'center',
              border: '1px solid rgba(255, 255, 255, 0.2)',
              boxShadow: '0 8px 32px rgba(0, 0, 0, 0.2)',
              animation: 'slideInUp 0.6s ease-out',
              maxWidth: '600px',
              width: '100%'
            }}>
              <div className="icon" style={{ fontSize: '5rem', marginBottom: '25px', opacity: 0.7 }}>üì∫</div>
              <h3 style={{ 
                color: 'white', 
                marginBottom: '20px',
                fontSize: '2rem',
                fontWeight: '700',
                textShadow: '0 2px 4px rgba(0, 0, 0, 0.3)'
              }}>
                ŸÑÿß ŸäŸàÿ¨ÿØ ÿ®ÿ´ ŸÖÿ®ÿßÿ¥ÿ± ÿ≠ÿßŸÑŸäÿßŸã
              </h3>
              <p style={{ 
                color: 'rgba(255, 255, 255, 0.8)',
                fontSize: '1.3rem',
                fontWeight: '500',
                marginBottom: '30px',
                lineHeight: '1.6'
              }}>
                ÿ™ÿ±ŸÇÿ® ÿßŸÑÿ•ÿπŸÑÿßŸÜ ÿπŸÜ ŸÖŸàÿßÿπŸäÿØ ÿßŸÑÿ®ÿ´ ÿßŸÑŸÖÿ®ÿßÿ¥ÿ± ŸÑŸÑŸÖÿ±ÿßÿ¨ÿπÿ© ÿßŸÑŸÜŸáÿßÿ¶Ÿäÿ©
              </p>
              
              {/* ŸÜÿµÿßÿ¶ÿ≠ ŸÑŸÑÿ∑ŸÑÿßÿ® */}
              <div style={{
                background: 'rgba(255, 255, 255, 0.1)',
                borderRadius: '15px',
                padding: '20px',
                marginTop: '25px'
              }}>
                <h4 style={{
                  color: 'white',
                  fontSize: '1.2rem',
                  fontWeight: '600',
                  marginBottom: '15px'
                }}>üí° ŸÜÿµÿßÿ¶ÿ≠ ŸÖŸáŸÖÿ©:</h4>
                <ul style={{
                  color: 'rgba(255, 255, 255, 0.9)',
                  fontSize: '1rem',
                  textAlign: 'right',
                  listStyle: 'none',
                  padding: 0
                }}>
                  <li style={{ marginBottom: '8px' }}>üîÑ ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿµŸÅÿ≠ÿ© ÿ™ŸÑŸÇÿßÿ¶ŸäÿßŸã ŸÉŸÑ 15 ÿ´ÿßŸÜŸäÿ©</li>
                  <li style={{ marginBottom: '8px' }}>üì± ŸäŸÖŸÉŸÜŸÉ ŸÖÿ¥ÿßŸáÿØÿ© ÿßŸÑÿ®ÿ´ ŸÖŸÜ ÿßŸÑŸáÿßÿ™ŸÅ ÿ£Ÿà ÿßŸÑŸÉŸÖÿ®ŸäŸàÿ™ÿ±</li>
                  <li>üîî ÿ≥ÿ™ÿ∏Ÿáÿ± ÿ•ÿ¥ÿßÿ±ÿ© ÿ≠ŸÖÿ±ÿßÿ° ÿπŸÜÿØ ÿ®ÿØÿ° ÿßŸÑÿ®ÿ´</li>
                </ul>
              </div>
            </div>
          )}
        </div>
      </div>

      <style>
        {`
          @keyframes spin {
            0% { transform: rotate(0deg); }
            100% { transform: rotate(360deg); }
          }
          
          @keyframes pulse {
            0%, 100% { 
              opacity: 1; 
              transform: scale(1);
            }
            50% { 
              opacity: 0.7; 
              transform: scale(1.2);
            }
          }
          
          @keyframes slideInUp {
            from {
              opacity: 0;
              transform: translateY(30px);
            }
            to {
              opacity: 1;
              transform: translateY(0);
            }
          }
          
          /* ÿ™ÿ≠ÿ≥ŸäŸÜ ŸÑŸÑÿ¥ÿßÿ¥ÿßÿ™ ÿßŸÑŸÉÿ®Ÿäÿ±ÿ© - ÿ™Ÿàÿ≥Ÿäÿ∑ ÿßŸÑŸÖÿ≠ÿ™ŸàŸâ */
          @media (min-width: 992px) {
            .live-content {
              max-width: 800px !important;
              margin: 0 auto !important;
            }
          }
          
          @media (min-width: 1200px) {
            .live-content {
              max-width: 900px !important;
            }
          }
          
          @media (max-width: 768px) {
            .live-content {
              padding: 20px 15px !important;
              max-width: 100% !important;
            }
            
            .empty-state {
              padding: 30px 20px !important;
              max-width: 100% !important;
            }
            
            .empty-state h3 {
              font-size: 1.6rem !important;
            }
            
            .empty-state p {
              font-size: 1.1rem !important;
            }
            
            .pulse-dot {
              width: 16px !important;
              height: 16px !important;
              top: 20px !important;
              right: 20px !important;
            }
            
            .header-section h1 {
              font-size: 2rem !important;
            }
            
            .header-section p {
              font-size: 1rem !important;
            }
          }
          
          @media (max-width: 480px) {
            .live-content {
              padding: 15px 10px !important;
            }
            
            .empty-state {
              padding: 25px 15px !important;
            }
            
            .empty-state h3 {
              font-size: 1.4rem !important;
            }
            
            .empty-state p {
              font-size: 1rem !important;
            }
            
            .empty-state .icon {
              font-size: 4rem !important;
              margin-bottom: 20px !important;
            }
            
            .header-section {
              padding: 20px 0 !important;
            }
            
            .header-section h1 {
              font-size: 1.8rem !important;
            }
            
            .header-section .icon {
              font-size: 2.5rem !important;
            }
            
            .pulse-dot {
              width: 14px !important;
              height: 14px !important;
              top: 15px !important;
              right: 15px !important;
            }
          }
          
          @media (max-width: 360px) {
            .live-content {
              padding: 12px 8px !important;
            }
            
            .empty-state {
              padding: 20px 12px !important;
            }
            
            .empty-state h3 {
              font-size: 1.2rem !important;
            }
            
            .empty-state p {
              font-size: 0.9rem !important;
            }
            
            .header-content {
              padding: 0 10px !important;
            }
            
            .header-content h1 {
              font-size: 1.4rem !important;
            }
            
            .header-content p {
              font-size: 0.9rem !important;
            }
            
            .pulse-dot {
              width: 12px !important;
              height: 12px !important;
              top: 12px !important;
              right: 12px !important;
            }
          }
          
          iframe:focus {
            outline: 3px solid rgba(255, 255, 255, 0.5);
            outline-offset: 2px;
          }
          
          @media (prefers-reduced-motion: reduce) {
            * {
              animation-duration: 0.01ms !important;
              animation-iteration-count: 1 !important;
              transition-duration: 0.01ms !important;
            }
          }
          
          /* ÿ™ÿ≠ÿ≥ŸäŸÜ ÿßŸÑÿ™ÿ®ÿßŸäŸÜ ŸÑŸÑÿ±ÿ§Ÿäÿ© */
          @media (prefers-contrast: high) {
            .live-content {
              border: 3px solid #fff !important;
            }
            
            .empty-state {
              border: 3px solid #fff !important;
            }
          }
          
          /* ÿ™ÿ≠ÿ≥ŸäŸÜ ŸÑŸÑÿ∑ÿ®ÿßÿπÿ© */
          @media print {
            .live-content iframe {
              display: none !important;
            }
            
            .live-content::after {
              content: "ÿßŸÑÿ®ÿ´ ÿßŸÑŸÖÿ®ÿßÿ¥ÿ± ŸÖÿ™ÿßÿ≠ ÿπŸÑŸâ ÿßŸÑŸÖŸàŸÇÿπ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä" !important;
              display: block !important;
              text-align: center !important;
              padding: 50px !important;
              color: #000 !important;
              background: #fff !important;
            }
          }
        `}
      </style>
    </>
  );
}

export default LiveGrade3;
