// src/components/AdminApprovalPanel.js
import React, { useState, useEffect } from 'react';

function AdminApprovalPanel() {
  const [registrations, setRegistrations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedImage, setSelectedImage] = useState(null);
  const [refreshing, setRefreshing] = useState(false);
  const [processingAction, setProcessingAction] = useState(null);

  // إعدادات GitHub المصححة
  const GITHUB_TOKEN = process.env.REACT_APP_GITHUB_PAT;
  const REPO_OWNER = 'omareldalil24';
  const REPO_NAME = 'PIONEER';
  const REGISTRATIONS_FILE_PATH = 'public/trash/control/registrations.json';

  // دالة لجلب البيانات من GitHub
  const fetchRegistrationsFromGitHub = async () => {
    try {
      console.log('📥 جاري جلب البيانات من GitHub...');
      
      const cacheBuster = Date.now();
      const response = await fetch(`https://raw.githubusercontent.com/${REPO_OWNER}/${REPO_NAME}/main/${REGISTRATIONS_FILE_PATH}?t=${cacheBuster}`);
      
      if (response.ok) {
        const data = await response.json();
        console.log('✅ تم جلب البيانات بنجاح:', data);
        console.log('📊 عدد التسجيلات:', data.registrations?.length || 0);
        console.log('👥 عدد الطلاب المقبولين:', data.approvedStudents?.length || 0);
        
        if (data.registrations && data.registrations.length > 0) {
          data.registrations.sort((a, b) => new Date(b.submissionDate) - new Date(a.submissionDate));
        }
        
        return data;
      } else {
        console.log('⚠️ فشل في جلب البيانات، status:', response.status);
        console.log('⚠️ Response text:', await response.text());
      }
    } catch (error) {
      console.error('❌ خطأ في جلب البيانات:', error);
    }
    
    return { registrations: [], approvedStudents: [] };
  };

  // دالة لحفظ البيانات إلى GitHub مع معالجة تضارب الإصدارات
  const saveRegistrationsToGitHub = async (data, retryCount = 0) => {
    const maxRetries = 3;
    
    try {
      console.log('💾 جاري حفظ البيانات في GitHub...');
      
      if (!GITHUB_TOKEN) {
        throw new Error('GITHUB_TOKEN غير موجود في متغيرات البيئة');
      }
      
      // نستخدم نفس طريقة جلب البيانات الأولية للحصول على SHA محدث
      let sha = null;
      try {
        console.log('🔄 جاري جلب أحدث إصدار من الملف...');
        
        // جلب البيانات الحديثة مع cache buster
        const cacheBuster = Date.now();
        const rawResponse = await fetch(`https://raw.githubusercontent.com/${REPO_OWNER}/${REPO_NAME}/main/${REGISTRATIONS_FILE_PATH}?t=${cacheBuster}`);
        
        if (rawResponse.ok) {
          // إذا تم العثور على الملف، نجلب SHA من API
          const apiResponse = await fetch(`https://api.github.com/repos/${REPO_OWNER}/${REPO_NAME}/contents/${REGISTRATIONS_FILE_PATH}`, {
            headers: {
              'Authorization': `Bearer ${GITHUB_TOKEN}`,
              'Accept': 'application/vnd.github.v3+json'
            }
          });
          
          if (apiResponse.ok) {
            const fileData = await apiResponse.json();
            sha = fileData.sha;
            console.log('🔑 تم الحصول على أحدث SHA:', sha);
          }
        } else {
          console.log('📝 الملف غير موجود، سيتم إنشاء ملف جديد');
        }
      } catch (shaError) {
        console.log('📝 لم يتم العثور على الملف، سيتم إنشاء ملف جديد');
      }

      const jsonContent = JSON.stringify(data, null, 2);
      const base64Content = btoa(unescape(encodeURIComponent(jsonContent)));

      const requestBody = {
        message: `تحديث البيانات - ${new Date().toLocaleString('ar-EG')} (محاولة ${retryCount + 1})`,
        content: base64Content,
        branch: 'main'
      };

      if (sha) {
        requestBody.sha = sha;
      }

      console.log('📤 إرسال البيانات مع SHA:', sha);
      const response = await fetch(`https://api.github.com/repos/${REPO_OWNER}/${REPO_NAME}/contents/${REGISTRATIONS_FILE_PATH}`, {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${GITHUB_TOKEN}`,
          'Content-Type': 'application/json',
          'Accept': 'application/vnd.github.v3+json'
        },
        body: JSON.stringify(requestBody)
      });

      if (response.ok) {
        console.log('✅ تم حفظ البيانات في GitHub بنجاح');
        return await response.json();
      } else {
        const errorData = await response.json();
        console.error('❌ فشل في حفظ البيانات:', errorData);
        
        // إذا كان خطأ تضارب SHA وهناك محاولات متبقية
        if (response.status === 409 && retryCount < maxRetries) {
          console.log(`🔄 محاولة إعادة الحفظ (${retryCount + 1}/${maxRetries})...`);
          await new Promise(resolve => setTimeout(resolve, 1000 * (retryCount + 1))); // انتظار متزايد
          return await saveRegistrationsToGitHub(data, retryCount + 1);
        }
        
        throw new Error(`فشل في حفظ البيانات: ${errorData.message || 'خطأ غير معروف'}`);
      }
    } catch (error) {
      // إذا كان خطأ شبكة وهناك محاولات متبقية
      if (retryCount < maxRetries && (error.name === 'TypeError' || error.message.includes('fetch') || error.message.includes('CORS'))) {
        console.log(`🔄 محاولة إعادة الاتصال (${retryCount + 1}/${maxRetries})...`);
        await new Promise(resolve => setTimeout(resolve, 2000 * (retryCount + 1)));
        return await saveRegistrationsToGitHub(data, retryCount + 1);
      }
      
      console.error('💥 خطأ في حفظ البيانات:', error);
      throw error;
    }
  };

  // جلب البيانات عند تحميل الصفحة فقط (بدون تحديث تلقائي)
  useEffect(() => {
    loadRegistrations();
  }, []);

  const loadRegistrations = async () => {
    setLoading(true);
    try {
      const data = await fetchRegistrationsFromGitHub();
      setRegistrations(data.registrations || []);
      console.log('📋 البيانات المحملة:', data.registrations || []);
    } catch (error) {
      console.error('خطأ في تحميل البيانات:', error);
      alert('❌ خطأ في تحميل البيانات. تحقق من الاتصال.');
    } finally {
      setLoading(false);
    }
  };

  const refreshData = async () => {
    console.log('🔄 تحديث البيانات يدوياً...');
    setRefreshing(true);
    try {
      await loadRegistrations();
      alert('✅ تم تحديث البيانات بنجاح!');
    } catch (error) {
      console.error('خطأ في التحديث:', error);
      alert('❌ خطأ في تحديث البيانات');
    } finally {
      setRefreshing(false);
    }
  };

  const handleApprove = async (id) => {
    if (processingAction === id) return;
    
    const confirmApprove = window.confirm('✅ هل أنت متأكد من قبول هذا الطلب؟\n\nسيتمكن الطالب من تسجيل الدخول والوصول للمحتوى.');
    if (!confirmApprove) return;

    setProcessingAction(id);
    try {
      console.log('✅ جاري قبول الطلب:', id);
      
      // جلب أحدث البيانات قبل التعديل مباشرة
      console.log('📥 جلب أحدث البيانات...');
      const currentData = await fetchRegistrationsFromGitHub();
      
      const targetRegistration = currentData.registrations.find(reg => reg.id === id);
      if (!targetRegistration) {
        throw new Error('لم يتم العثور على التسجيل');
      }
      
      const updatedRegistrations = currentData.registrations.map(reg =>
        reg.id === id ? { 
          ...reg, 
          status: 'approved', 
          approvalDate: new Date().toISOString(),
          approvedBy: 'admin'
        } : reg
      );
      
      if (!currentData.approvedStudents) {
        currentData.approvedStudents = [];
      }
      
      const existingStudent = currentData.approvedStudents.find(s => s.id === id);
      if (!existingStudent) {
        const newStudent = {
          id: targetRegistration.id,
          name: targetRegistration.studentName,
          approvalDate: new Date().toISOString(),
          approvedBy: 'admin'
        };
        currentData.approvedStudents.push(newStudent);
        console.log('👤 تم إضافة الطالب للقائمة المقبولة:', newStudent);
      }
      
      const updatedData = {
        registrations: updatedRegistrations,
        approvedStudents: currentData.approvedStudents
      };
      
      console.log('💾 حفظ البيانات المحدثة...');
      await saveRegistrationsToGitHub(updatedData);
      
      // تحديث الواجهة المحلية
      setRegistrations(updatedRegistrations);
      
      alert(`✅ تم قبول طلب "${targetRegistration.studentName}" بنجاح!\n\n🎓 يمكن للطالب الآن تسجيل الدخول والوصول للمحتوى.`);
      console.log('🎉 تم قبول الطلب بنجاح');
    } catch (error) {
      console.error('❌ خطأ في قبول الطلب:', error);
      alert(`❌ حدث خطأ أثناء قبول الطلب: ${error.message}\n\nيرجى المحاولة مرة أخرى.`);
    } finally {
      setProcessingAction(null);
    }
  };

  const handleReject = async (id) => {
    if (processingAction === id) return;
    
    const confirmReject = window.confirm('❌ هل أنت متأكد من رفض هذا الطلب؟\n\nسيتم تغيير حالة الطلب إلى "مرفوض".');
    if (!confirmReject) return;
    
    setProcessingAction(id);
    try {
      console.log('❌ جاري رفض الطلب:', id);
      
      // جلب أحدث البيانات قبل التعديل مباشرة
      console.log('📥 جلب أحدث البيانات...');
      const currentData = await fetchRegistrationsFromGitHub();
      
      const targetRegistration = currentData.registrations.find(reg => reg.id === id);
      if (!targetRegistration) {
        throw new Error('لم يتم العثور على التسجيل');
      }
      
      const updatedRegistrations = currentData.registrations.map(reg =>
        reg.id === id ? { 
          ...reg, 
          status: 'rejected', 
          rejectionDate: new Date().toISOString(),
          rejectedBy: 'admin'
        } : reg
      );
      
      const updatedApprovedStudents = currentData.approvedStudents ? 
        currentData.approvedStudents.filter(student => student.id !== id) : [];
      
      const updatedData = {
        registrations: updatedRegistrations,
        approvedStudents: updatedApprovedStudents
      };
      
      console.log('💾 حفظ البيانات المحدثة...');
      await saveRegistrationsToGitHub(updatedData);
      
      // تحديث الواجهة المحلية
      setRegistrations(updatedRegistrations);
      
      alert(`❌ تم رفض طلب "${targetRegistration.studentName}".`);
      console.log('📝 تم رفض الطلب');
    } catch (error) {
      console.error('❌ خطأ في رفض الطلب:', error);
      alert(`❌ حدث خطأ أثناء رفض الطلب: ${error.message}\n\nيرجى المحاولة مرة أخرى.`);
    } finally {
      setProcessingAction(null);
    }
  };

  const handleDelete = async (id) => {
    if (processingAction === id) return;
    
    const registration = registrations.find(reg => reg.id === id);
    const confirmDelete = window.confirm(`🗑️ هل أنت متأكد من حذف طلب "${registration?.studentName}" نهائياً؟\n\n⚠️ هذا الإجراء لا يمكن التراجع عنه!`);
    if (!confirmDelete) return;

    setProcessingAction(id);
    try {
      console.log('🗑️ جاري حذف الطلب:', id);
      
      // جلب أحدث البيانات قبل التعديل مباشرة
      console.log('📥 جلب أحدث البيانات...');
      const currentData = await fetchRegistrationsFromGitHub();
      
      const updatedRegistrations = currentData.registrations.filter(reg => reg.id !== id);
      
      const updatedApprovedStudents = currentData.approvedStudents ? 
        currentData.approvedStudents.filter(student => student.id !== id) : [];
      
      const updatedData = {
        registrations: updatedRegistrations,
        approvedStudents: updatedApprovedStudents
      };
      
      console.log('💾 حفظ البيانات المحدثة...');
      await saveRegistrationsToGitHub(updatedData);
      
      // تحديث الواجهة المحلية
      setRegistrations(updatedRegistrations);
      
      alert(`🗑️ تم حذف طلب "${registration?.studentName}" نهائياً.`);
      console.log('🗑️ تم حذف الطلب نهائياً');
    } catch (error) {
      console.error('❌ خطأ في حذف الطلب:', error);
      alert(`❌ حدث خطأ أثناء حذف الطلب: ${error.message}\n\nيرجى المحاولة مرة أخرى.`);
    } finally {
      setProcessingAction(null);
    }
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('ar-EG', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'pending': return '#f39c12';
      case 'approved': return '#00b894';
      case 'rejected': return '#e74c3c';
      default: return '#6c757d';
    }
  };

  const getStatusText = (status) => {
    switch (status) {
      case 'pending': return 'في الانتظار';
      case 'approved': return 'مقبول';
      case 'rejected': return 'مرفوض';
      default: return 'غير محدد';
    }
  };

  const pendingCount = registrations.filter(reg => reg.status === 'pending').length;
  const approvedCount = registrations.filter(reg => reg.status === 'approved').length;
  const rejectedCount = registrations.filter(reg => reg.status === 'rejected').length;

  if (loading) {
    return (
      <div style={{
        minHeight: '100vh',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center'
      }}>
        <div style={{ textAlign: 'center', color: 'white' }}>
          <div className="loading-spinner" style={{
            width: '60px',
            height: '60px',
            border: '4px solid rgba(255, 255, 255, 0.3)',
            borderTop: '4px solid #c31432',
            borderRadius: '50%',
            animation: 'spin 1s linear infinite',
            margin: '0 auto 25px'
          }}></div>
          <h3 style={{ 
            fontWeight: '700', 
            fontSize: '1.8rem',
            textShadow: '0 2px 4px rgba(0, 0, 0, 0.3)'
          }}>
            📥 جاري تحميل الطلبات...
          </h3>
          <p style={{ opacity: 0.9, fontSize: '1.1rem' }}>
            قد يستغرق هذا بضع ثوان
          </p>
        </div>
      </div>
    );
  }

  return (
    <>
      <div style={{
        minHeight: '100vh',
        background: 'linear-gradient(to bottom right, #c31432, #8e0000, #240b36)',
        backgroundAttachment: 'fixed',
        paddingTop: '100px',
        padding: '100px 20px 20px 20px',
        fontFamily: "'Segoe UI', 'Roboto', 'Helvetica', 'Arial', sans-serif"
      }}>
        <div style={{ maxWidth: '1200px', margin: '0 auto' }}>
          {/* العنوان والإحصائيات */}
          <div style={{
            background: 'rgba(255, 255, 255, 0.95)',
            backdropFilter: 'blur(20px)',
            borderRadius: '25px',
            padding: '35px',
            marginBottom: '30px',
            textAlign: 'center',
            boxShadow: '0 20px 60px rgba(0, 0, 0, 0.1)',
            border: '1px solid rgba(255, 255, 255, 0.2)',
            animation: 'slideInUp 0.6s ease-out'
          }}>
            <div style={{ 
              display: 'flex', 
              justifyContent: 'space-between', 
              alignItems: 'center', 
              marginBottom: '25px', 
              flexWrap: 'wrap', 
              gap: '15px' 
            }}>
              <div>
                <h1 style={{ 
                  color: '#2c3e50', 
                  margin: 0, 
                  fontSize: '2.5rem',
                  fontWeight: '700',
                  background: 'linear-gradient(135deg, #c31432, #8e0000)',
                  backgroundClip: 'text',
                  WebkitBackgroundClip: 'text',
                  WebkitTextFillColor: 'transparent',
                  textShadow: 'none'
                }}>
                  🎓 لوحة تحكم طلبات الاشتراك
                </h1>
              </div>
              <div style={{ display: 'flex', gap: '12px', alignItems: 'center', flexWrap: 'wrap' }}>
                <button
                  onClick={refreshData}
                  disabled={refreshing}
                  style={{
                    background: refreshing ? 
                      'linear-gradient(135deg, #6c757d, #5a6268)' : 
                      'linear-gradient(135deg, #c31432, #8e0000)',
                    color: 'white',
                    border: 'none',
                    borderRadius: '15px',
                    padding: '12px 24px',
                    fontSize: '1rem',
                    cursor: refreshing ? 'not-allowed' : 'pointer',
                    opacity: refreshing ? 0.7 : 1,
                    transition: 'all 0.3s ease',
                    fontWeight: '600',
                    boxShadow: '0 6px 20px rgba(195, 20, 50, 0.3)'
                  }}
                  onMouseEnter={(e) => {
                    if (!refreshing) {
                      e.target.style.transform = 'translateY(-2px)';
                      e.target.style.boxShadow = '0 10px 30px rgba(195, 20, 50, 0.4)';
                    }
                  }}
                  onMouseLeave={(e) => {
                    if (!refreshing) {
                      e.target.style.transform = 'translateY(0)';
                      e.target.style.boxShadow = '0 6px 20px rgba(195, 20, 50, 0.3)';
                    }
                  }}
                >
                  {refreshing ? (
                    <>
                      <span style={{ 
                        display: 'inline-block',
                        width: '16px',
                        height: '16px',
                        border: '2px solid #ffffff',
                        borderTop: '2px solid transparent',
                        borderRadius: '50%',
                        animation: 'spin 1s linear infinite',
                        marginLeft: '8px'
                      }}></span>
                      جاري التحديث...
                    </>
                  ) : (
                    '🔄 تحديث البيانات'
                  )}
                </button>
                <button
                  onClick={() => {
                    console.log('🔍 معلومات التشخيص:');
                    console.log('- عدد التسجيلات الحالية:', registrations.length);
                    console.log('- التسجيلات:', registrations);
                    console.log('- إعدادات GitHub:', { REPO_OWNER, REPO_NAME, REGISTRATIONS_FILE_PATH });
                    console.log('- GITHUB_TOKEN متوفر:', !!GITHUB_TOKEN);
                    alert('تم طباعة معلومات التشخيص في Console (اضغط F12)');
                  }}
                  style={{
                    background: 'linear-gradient(135deg, #6c757d, #495057)',
                    color: 'white',
                    border: 'none',
                    borderRadius: '15px',
                    padding: '12px 24px',
                    fontSize: '1rem',
                    cursor: 'pointer',
                    fontWeight: '600',
                    transition: 'all 0.3s ease',
                    boxShadow: '0 6px 20px rgba(108, 117, 125, 0.3)'
                  }}
                  onMouseEnter={(e) => {
                    e.target.style.transform = 'translateY(-2px)';
                    e.target.style.boxShadow = '0 10px 30px rgba(108, 117, 125, 0.4)';
                  }}
                  onMouseLeave={(e) => {
                    e.target.style.transform = 'translateY(0)';
                    e.target.style.boxShadow = '0 6px 20px rgba(108, 117, 125, 0.3)';
                  }}
                >
                  🔍 فحص البيانات
                </button>
              </div>
            </div>
            
            <div style={{
              display: 'grid',
              gridTemplateColumns: 'repeat(auto-fit, minmax(220px, 1fr))',
              gap: '25px',
              marginTop: '25px'
            }}>
              <div style={{
                background: 'linear-gradient(135deg, #f39c12, #e67e22)',
                color: 'white',
                padding: '25px',
                borderRadius: '20px',
                textAlign: 'center',
                boxShadow: '0 10px 30px rgba(243, 156, 18, 0.3)',
                transition: 'transform 0.3s ease'
              }}
              onMouseEnter={(e) => e.target.style.transform = 'translateY(-5px)'}
              onMouseLeave={(e) => e.target.style.transform = 'translateY(0)'}
              >
                <h3 style={{ margin: '0 0 12px 0', fontSize: '2.2rem', fontWeight: '700' }}>{pendingCount}</h3>
                <p style={{ margin: 0, fontSize: '1.1rem', fontWeight: '600' }}>⏳ في الانتظار</p>
              </div>
              
              <div style={{
                background: 'linear-gradient(135deg, #00b894, #00a085)',
                color: 'white',
                padding: '25px',
                borderRadius: '20px',
                textAlign: 'center',
                boxShadow: '0 10px 30px rgba(0, 184, 148, 0.3)',
                transition: 'transform 0.3s ease'
              }}
              onMouseEnter={(e) => e.target.style.transform = 'translateY(-5px)'}
              onMouseLeave={(e) => e.target.style.transform = 'translateY(0)'}
              >
                <h3 style={{ margin: '0 0 12px 0', fontSize: '2.2rem', fontWeight: '700' }}>{approvedCount}</h3>
                <p style={{ margin: 0, fontSize: '1.1rem', fontWeight: '600' }}>✅ مقبول</p>
              </div>
              
              <div style={{
                background: 'linear-gradient(135deg, #e74c3c, #c0392b)',
                color: 'white',
                padding: '25px',
                borderRadius: '20px',
                textAlign: 'center',
                boxShadow: '0 10px 30px rgba(231, 76, 60, 0.3)',
                transition: 'transform 0.3s ease'
              }}
              onMouseEnter={(e) => e.target.style.transform = 'translateY(-5px)'}
              onMouseLeave={(e) => e.target.style.transform = 'translateY(0)'}
              >
                <h3 style={{ margin: '0 0 12px 0', fontSize: '2.2rem', fontWeight: '700' }}>{rejectedCount}</h3>
                <p style={{ margin: 0, fontSize: '1.1rem', fontWeight: '600' }}>❌ مرفوض</p>
              </div>
              
              <div style={{
                background: 'linear-gradient(135deg, #17a2b8, #138496)',
                color: 'white',
                padding: '25px',
                borderRadius: '20px',
                textAlign: 'center',
                boxShadow: '0 10px 30px rgba(23, 162, 184, 0.3)',
                transition: 'transform 0.3s ease'
              }}
              onMouseEnter={(e) => e.target.style.transform = 'translateY(-5px)'}
              onMouseLeave={(e) => e.target.style.transform = 'translateY(0)'}
              >
                <h3 style={{ margin: '0 0 12px 0', fontSize: '2.2rem', fontWeight: '700' }}>{registrations.length}</h3>
                <p style={{ margin: 0, fontSize: '1.1rem', fontWeight: '600' }}>📊 المجموع</p>
              </div>
            </div>
          </div>

          {/* قائمة الطلبات */}
          <div style={{
            display: 'grid',
            gap: '20px'
          }}>
            {registrations.map((registration, index) => (
              <div key={registration.id} style={{
                background: 'rgba(255, 255, 255, 0.95)',
                backdropFilter: 'blur(20px)',
                borderRadius: '20px',
                padding: '30px',
                display: 'grid',
                gridTemplateColumns: '1fr auto',
                gap: '25px',
                alignItems: 'center',
                direction: 'rtl',
                border: `3px solid ${getStatusColor(registration.status)}`,
                position: 'relative',
                boxShadow: '0 15px 35px rgba(0, 0, 0, 0.08)',
                transition: 'all 0.3s ease',
                animation: `slideInUp 0.6s ease-out ${index * 0.1}s both`
              }}
              onMouseEnter={(e) => {
                e.target.style.transform = 'translateY(-3px)';
                e.target.style.boxShadow = '0 20px 40px rgba(0, 0, 0, 0.12)';
              }}
              onMouseLeave={(e) => {
                e.target.style.transform = 'translateY(0)';
                e.target.style.boxShadow = '0 15px 35px rgba(0, 0, 0, 0.08)';
              }}
              >
                {processingAction === registration.id && (
                  <div style={{
                    position: 'absolute',
                    top: 0,
                    left: 0,
                    right: 0,
                    bottom: 0,
                    background: 'rgba(255, 255, 255, 0.9)',
                    backdropFilter: 'blur(5px)',
                    borderRadius: '20px',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    zIndex: 10
                  }}>
                    <div style={{ textAlign: 'center' }}>
                      <div style={{
                        width: '40px',
                        height: '40px',
                        border: '4px solid #c31432',
                        borderTop: '4px solid transparent',
                        borderRadius: '50%',
                        animation: 'spin 1s linear infinite',
                        margin: '0 auto 15px'
                      }}></div>
                      <p style={{ color: '#2c3e50', fontWeight: '700', fontSize: '1.1rem' }}>جاري المعالجة...</p>
                    </div>
                  </div>
                )}
                
                <div>
                  <h3 style={{ 
                    color: '#2c3e50', 
                    marginBottom: '12px',
                    fontSize: '1.5rem',
                    fontWeight: '700'
                  }}>
                    👤 {registration.studentName}
                  </h3>
                  
                  <p style={{ 
                    color: '#6c757d', 
                    marginBottom: '18px',
                    fontSize: '1rem',
                    fontWeight: '500'
                  }}>
                    📅 تاريخ التقديم: {formatDate(registration.submissionDate)}
                  </p>
                  
                  <div style={{ 
                    display: 'flex', 
                    alignItems: 'center', 
                    gap: '18px',
                    flexWrap: 'wrap'
                  }}>
                    <span style={{
                      background: `linear-gradient(135deg, ${getStatusColor(registration.status)}, ${getStatusColor(registration.status)}dd)`,
                      color: 'white',
                      padding: '10px 20px',
                      borderRadius: '25px',
                      fontSize: '0.9rem',
                      fontWeight: '700',
                      textShadow: '0 1px 2px rgba(0, 0, 0, 0.2)',
                      boxShadow: `0 4px 15px ${getStatusColor(registration.status)}40`
                    }}>
                      {getStatusText(registration.status)}
                    </span>
                    
                    {registration.paymentReceiptBase64 ? (
                      <button
                        onClick={() => setSelectedImage(`data:${registration.paymentReceiptType};base64,${registration.paymentReceiptBase64}`)}
                        style={{
                          background: 'linear-gradient(135deg, #17a2b8, #138496)',
                          color: 'white',
                          border: 'none',
                          padding: '12px 24px',
                          borderRadius: '12px',
                          cursor: 'pointer',
                          fontSize: '0.9rem',
                          fontWeight: '600',
                          transition: 'all 0.3s ease',
                          boxShadow: '0 6px 20px rgba(23, 162, 184, 0.3)'
                        }}
                        onMouseEnter={(e) => {
                          e.target.style.transform = 'scale(1.05)';
                          e.target.style.boxShadow = '0 8px 25px rgba(23, 162, 184, 0.4)';
                        }}
                        onMouseLeave={(e) => {
                          e.target.style.transform = 'scale(1)';
                          e.target.style.boxShadow = '0 6px 20px rgba(23, 162, 184, 0.3)';
                        }}
                      >
                        📄 عرض الوصل
                      </button>
                    ) : (
                      <span style={{ 
                        color: '#6c757d', 
                        fontSize: '0.9rem',
                        padding: '12px 20px',
                        background: 'rgba(108, 117, 125, 0.1)',
                        borderRadius: '12px'
                      }}>
                        📄 لا توجد صورة
                      </span>
                    )}
                  </div>
                  
                  {registration.status === 'approved' && registration.approvalDate && (
                    <p style={{ 
                      color: '#00b894', 
                      fontSize: '0.9rem', 
                      marginTop: '12px',
                      fontWeight: '600',
                      padding: '8px 16px',
                      background: 'rgba(0, 184, 148, 0.1)',
                      borderRadius: '8px',
                      display: 'inline-block'
                    }}>
                      ✅ تم القبول في: {formatDate(registration.approvalDate)}
                    </p>
                  )}
                  
                  {registration.status === 'rejected' && registration.rejectionDate && (
                    <p style={{ 
                      color: '#e74c3c', 
                      fontSize: '0.9rem', 
                      marginTop: '12px',
                      fontWeight: '600',
                      padding: '8px 16px',
                      background: 'rgba(231, 76, 60, 0.1)',
                      borderRadius: '8px',
                      display: 'inline-block'
                    }}>
                      ❌ تم الرفض في: {formatDate(registration.rejectionDate)}
                    </p>
                  )}
                </div>
                
                <div style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
                  {registration.status === 'pending' && (
                    <>
                      <button
                        onClick={() => handleApprove(registration.id)}
                        disabled={processingAction === registration.id}
                        style={{
                          background: 'linear-gradient(135deg, #00b894, #00a085)',
                          color: 'white',
                          border: 'none',
                          padding: '12px 24px',
                          borderRadius: '12px',
                          cursor: processingAction === registration.id ? 'not-allowed' : 'pointer',
                          fontSize: '0.9rem',
                          fontWeight: '700',
                          transition: 'all 0.3s ease',
                          opacity: processingAction === registration.id ? 0.6 : 1,
                          boxShadow: '0 6px 20px rgba(0, 184, 148, 0.3)'
                        }}
                        onMouseEnter={(e) => {
                          if (processingAction !== registration.id) {
                            e.target.style.transform = 'scale(1.05)';
                            e.target.style.boxShadow = '0 8px 25px rgba(0, 184, 148, 0.4)';
                          }
                        }}
                        onMouseLeave={(e) => {
                          if (processingAction !== registration.id) {
                            e.target.style.transform = 'scale(1)';
                            e.target.style.boxShadow = '0 6px 20px rgba(0, 184, 148, 0.3)';
                          }
                        }}
                      >
                        ✅ قبول
                      </button>
                      
                      <button
                        onClick={() => handleReject(registration.id)}
                        disabled={processingAction === registration.id}
                        style={{
                          background: 'linear-gradient(135deg, #e74c3c, #c0392b)',
                          color: 'white',
                          border: 'none',
                          padding: '12px 24px',
                          borderRadius: '12px',
                          cursor: processingAction === registration.id ? 'not-allowed' : 'pointer',
                          fontSize: '0.9rem',
                          fontWeight: '700',
                          transition: 'all 0.3s ease',
                          opacity: processingAction === registration.id ? 0.6 : 1,
                          boxShadow: '0 6px 20px rgba(231, 76, 60, 0.3)'
                        }}
                        onMouseEnter={(e) => {
                          if (processingAction !== registration.id) {
                            e.target.style.transform = 'scale(1.05)';
                            e.target.style.boxShadow = '0 8px 25px rgba(231, 76, 60, 0.4)';
                          }
                        }}
                        onMouseLeave={(e) => {
                          if (processingAction !== registration.id) {
                            e.target.style.transform = 'scale(1)';
                            e.target.style.boxShadow = '0 6px 20px rgba(231, 76, 60, 0.3)';
                          }
                        }}
                      >
                        ❌ رفض
                      </button>
                    </>
                  )}
                  
                  <button
                    onClick={() => handleDelete(registration.id)}
                    disabled={processingAction === registration.id}
                    style={{
                      background: 'linear-gradient(135deg, #6c757d, #495057)',
                      color: 'white',
                      border: 'none',
                      padding: registration.status === 'pending' ? '10px 20px' : '12px 24px',
                      borderRadius: '12px',
                      cursor: processingAction === registration.id ? 'not-allowed' : 'pointer',
                      fontSize: registration.status === 'pending' ? '0.8rem' : '0.9rem',
                      fontWeight: '700',
                      transition: 'all 0.3s ease',
                      opacity: processingAction === registration.id ? 0.6 : 1,
                      boxShadow: '0 6px 20px rgba(108, 117, 125, 0.3)'
                    }}
                    onMouseEnter={(e) => {
                      if (processingAction !== registration.id) {
                        e.target.style.transform = 'scale(1.05)';
                        e.target.style.boxShadow = '0 8px 25px rgba(108, 117, 125, 0.4)';
                      }
                    }}
                    onMouseLeave={(e) => {
                      if (processingAction !== registration.id) {
                        e.target.style.transform = 'scale(1)';
                        e.target.style.boxShadow = '0 6px 20px rgba(108, 117, 125, 0.3)';
                      }
                    }}
                  >
                    🗑️ {registration.status === 'pending' ? 'حذف' : 'حذف نهائي'}
                  </button>
                </div>
              </div>
            ))}
          </div>

          {registrations.length === 0 && (
            <div style={{
              background: 'rgba(255, 255, 255, 0.95)',
              backdropFilter: 'blur(20px)',
              borderRadius: '25px',
              padding: '50px',
              textAlign: 'center',
              boxShadow: '0 20px 60px rgba(0, 0, 0, 0.1)',
              animation: 'slideInUp 0.6s ease-out'
            }}>
              <div style={{ fontSize: '5rem', marginBottom: '25px' }}>📝</div>
              <h3 style={{ 
                color: '#6c757d', 
                fontSize: '1.6rem', 
                marginBottom: '18px',
                fontWeight: '700'
              }}>
                لا توجد طلبات حالياً
              </h3>
              <p style={{ 
                color: '#7f8c8d', 
                marginBottom: '25px',
                fontSize: '1.1rem'
              }}>
                سيتم عرض الطلبات هنا عند تقديمها من الطلاب
              </p>
              <div style={{ display: 'flex', gap: '15px', justifyContent: 'center', flexWrap: 'wrap' }}>
                <button
                  onClick={refreshData}
                  style={{
                    background: 'linear-gradient(135deg, #c31432, #8e0000)',
                    color: 'white',
                    border: 'none',
                    borderRadius: '15px',
                    padding: '15px 30px',
                    fontSize: '1rem',
                    cursor: 'pointer',
                    fontWeight: '700',
                    transition: 'all 0.3s ease',
                    boxShadow: '0 8px 25px rgba(195, 20, 50, 0.3)'
                  }}
                  onMouseEnter={(e) => {
                    e.target.style.transform = 'translateY(-2px)';
                    e.target.style.boxShadow = '0 12px 30px rgba(195, 20, 50, 0.4)';
                  }}
                  onMouseLeave={(e) => {
                    e.target.style.transform = 'translateY(0)';
                    e.target.style.boxShadow = '0 8px 25px rgba(195, 20, 50, 0.3)';
                  }}
                >
                  🔄 تحديث البيانات
                </button>
                <button
                  onClick={() => {
                    const testData = {
                      registrations: [
                        {
                          id: Date.now(),
                          studentName: 'طالب تجريبي',
                          paymentReceiptBase64: '',
                          paymentReceiptFileName: 'test.jpg',
                          paymentReceiptType: 'image/jpeg',
                          submissionDate: new Date().toISOString(),
                          status: 'pending'
                        }
                      ],
                      approvedStudents: []
                    };
                    console.log('🧪 بيانات تجريبية:', testData);
                    alert('تم إنشاء بيانات تجريبية في Console (اضغط F12)');
                  }}
                  style={{
                    background: 'linear-gradient(135deg, #f39c12, #e67e22)',
                    color: 'white',
                    border: 'none',
                    borderRadius: '15px',
                    padding: '15px 30px',
                    fontSize: '1rem',
                    cursor: 'pointer',
                    fontWeight: '700',
                    transition: 'all 0.3s ease',
                    boxShadow: '0 8px 25px rgba(243, 156, 18, 0.3)'
                  }}
                  onMouseEnter={(e) => {
                    e.target.style.transform = 'translateY(-2px)';
                    e.target.style.boxShadow = '0 12px 30px rgba(243, 156, 18, 0.4)';
                  }}
                  onMouseLeave={(e) => {
                    e.target.style.transform = 'translateY(0)';
                    e.target.style.boxShadow = '0 8px 25px rgba(243, 156, 18, 0.3)';
                  }}
                >
                  🧪 بيانات تجريبية
                </button>
              </div>
            </div>
          )}
        </div>
      </div>

      {/* مودال عرض الصورة */}
      {selectedImage && (
        <div
          style={{
            position: 'fixed',
            top: 0,
            left: 0,
            width: '100%',
            height: '100%',
            background: 'rgba(0, 0, 0, 0.9)',
            backdropFilter: 'blur(5px)',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            zIndex: 9999,
            animation: 'fadeIn 0.3s ease-out'
          }}
          onClick={() => setSelectedImage(null)}
        >
          <div
            style={{
              position: 'relative',
              maxWidth: '90%',
              maxHeight: '90%',
              background: 'rgba(255, 255, 255, 0.95)',
              backdropFilter: 'blur(20px)',
              borderRadius: '20px',
              padding: '25px',
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
              boxShadow: '0 25px 50px rgba(0, 0, 0, 0.3)'
            }}
            onClick={e => e.stopPropagation()}
          >
            <button
              onClick={() => setSelectedImage(null)}
              style={{
                position: 'absolute',
                top: '15px',
                right: '15px',
                background: 'linear-gradient(135deg, #e74c3c, #c0392b)',
                color: 'white',
                border: 'none',
                borderRadius: '50%',
                width: '45px',
                height: '45px',
                cursor: 'pointer',
                fontSize: '1.4rem',
                fontWeight: '700',
                zIndex: 10,
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                transition: 'all 0.3s ease',
                boxShadow: '0 6px 20px rgba(231, 76, 60, 0.3)'
              }}
              onMouseEnter={(e) => {
                e.target.style.transform = 'scale(1.1)';
                e.target.style.boxShadow = '0 8px 25px rgba(231, 76, 60, 0.4)';
              }}
              onMouseLeave={(e) => {
                e.target.style.transform = 'scale(1)';
                e.target.style.boxShadow = '0 6px 20px rgba(231, 76, 60, 0.3)';
              }}
            >
              ✕
            </button>
            
            <h3 style={{ 
              color: '#2c3e50', 
              marginBottom: '20px', 
              marginTop: '10px',
              fontSize: '1.4rem',
              fontWeight: '700'
            }}>
              📄 وصل الدفع
            </h3>
            
            <div style={{
              maxWidth: '600px',
              maxHeight: '600px',
              overflow: 'hidden',
              borderRadius: '15px',
              border: '3px solid rgba(195, 20, 50, 0.2)',
              position: 'relative',
              boxShadow: '0 10px 30px rgba(0, 0, 0, 0.1)'
            }}>
              <img
                src={selectedImage}
                alt="وصل الدفع"
                style={{
                  width: '100%',
                  height: 'auto',
                  maxHeight: '600px',
                  objectFit: 'contain',
                  display: 'block'
                }}
                onError={(e) => {
                  e.target.style.display = 'none';
                  const errorDiv = document.createElement('div');
                  errorDiv.style.cssText = `
                    display: flex;
                    flex-direction: column;
                    align-items: center;
                    justify-content: center;
                    padding: 40px;
                    color: #6c757d;
                    min-height: 200px;
                    text-align: center;
                    font-family: 'Segoe UI', 'Roboto', 'Helvetica', 'Arial', sans-serif;
                  `;
                  errorDiv.innerHTML = `
                    <div style="font-size: 3rem; margin-bottom: 15px;">❌</div>
                    <h4 style="margin-bottom: 10px; font-weight: 700;">خطأ في تحميل الصورة</h4>
                    <p>لا يمكن عرض وصل الدفع حالياً</p>
                  `;
                  e.target.parentNode.appendChild(errorDiv);
                }}
                onLoad={() => {
                  console.log('✅ تم تحميل الصورة بنجاح');
                }}
              />
            </div>
            
            <div style={{ 
              marginTop: '20px', 
              display: 'flex', 
              gap: '12px',
              flexWrap: 'wrap',
              justifyContent: 'center'
            }}>
              <button
                onClick={() => {
                  try {
                    window.open(selectedImage, '_blank');
                  } catch (error) {
                    console.error('خطأ في فتح الصورة:', error);
                    alert('❌ لا يمكن فتح الصورة في نافذة جديدة');
                  }
                }}
                style={{
                  background: 'linear-gradient(135deg, #c31432, #8e0000)',
                  color: 'white',
                  border: 'none',
                  borderRadius: '12px',
                  padding: '12px 24px',
                  cursor: 'pointer',
                  fontSize: '0.9rem',
                  fontWeight: '700',
                  transition: 'all 0.3s ease',
                  boxShadow: '0 6px 20px rgba(195, 20, 50, 0.3)'
                }}
                onMouseEnter={(e) => {
                  e.target.style.transform = 'scale(1.05)';
                  e.target.style.boxShadow = '0 8px 25px rgba(195, 20, 50, 0.4)';
                }}
                onMouseLeave={(e) => {
                  e.target.style.transform = 'scale(1)';
                  e.target.style.boxShadow = '0 6px 20px rgba(195, 20, 50, 0.3)';
                }}
              >
                🔗 فتح في نافذة جديدة
              </button>
              
              <button
                onClick={() => {
                  try {
                    const link = document.createElement('a');
                    link.href = selectedImage;
                    link.download = `payment_receipt_${Date.now()}.jpg`;
                    document.body.appendChild(link);
                    link.click();
                    document.body.removeChild(link);
                    console.log('📥 تم تحميل الصورة');
                  } catch (error) {
                    console.error('خطأ في تحميل الصورة:', error);
                    alert('❌ لا يمكن تحميل الصورة');
                  }
                }}
                style={{
                  background: 'linear-gradient(135deg, #00b894, #00a085)',
                  color: 'white',
                  border: 'none',
                  borderRadius: '12px',
                  padding: '12px 24px',
                  cursor: 'pointer',
                  fontSize: '0.9rem',
                  fontWeight: '700',
                  transition: 'all 0.3s ease',
                  boxShadow: '0 6px 20px rgba(0, 184, 148, 0.3)'
                }}
                onMouseEnter={(e) => {
                  e.target.style.transform = 'scale(1.05)';
                  e.target.style.boxShadow = '0 8px 25px rgba(0, 184, 148, 0.4)';
                }}
                onMouseLeave={(e) => {
                  e.target.style.transform = 'scale(1)';
                  e.target.style.boxShadow = '0 6px 20px rgba(0, 184, 148, 0.3)';
                }}
              >
                📥 تحميل الصورة
              </button>
              
              <button
                onClick={() => {
                  try {
                    navigator.clipboard.writeText(selectedImage);
                    alert('✅ تم نسخ رابط الصورة للحافظة');
                  } catch (error) {
                    console.error('خطأ في نسخ الرابط:', error);
                    alert('❌ لا يمكن نسخ الرابط');
                  }
                }}
                style={{
                  background: 'linear-gradient(135deg, #a29bfe, #6c5ce7)',
                  color: 'white',
                  border: 'none',
                  borderRadius: '12px',
                  padding: '12px 24px',
                  cursor: 'pointer',
                  fontSize: '0.9rem',
                  fontWeight: '700',
                  transition: 'all 0.3s ease',
                  boxShadow: '0 6px 20px rgba(162, 155, 254, 0.3)'
                }}
                onMouseEnter={(e) => {
                  e.target.style.transform = 'scale(1.05)';
                  e.target.style.boxShadow = '0 8px 25px rgba(162, 155, 254, 0.4)';
                }}
                onMouseLeave={(e) => {
                  e.target.style.transform = 'scale(1)';
                  e.target.style.boxShadow = '0 6px 20px rgba(162, 155, 254, 0.3)';
                }}
              >
                📋 نسخ الرابط
              </button>
            </div>
          </div>
        </div>
      )}

      <style>
        {`
          @keyframes spin {
            0% { transform: rotate(0deg); }
            100% { transform: rotate(360deg); }
          }
          
          @keyframes pulse {
            0% { opacity: 1; transform: scale(1); }
            50% { opacity: 0.7; transform: scale(1.05); }
            100% { opacity: 1; transform: scale(1); }
          }
          
          @keyframes fadeIn {
            from { opacity: 0; }
            to { opacity: 1; }
          }
          
          @keyframes slideInUp {
            from { 
              opacity: 0; 
              transform: translateY(30px); 
            }
            to { 
              opacity: 1; 
              transform: translateY(0); 
            }
          }
          
          /* تحسين الاستجابة للموبايل */
          @media (max-width: 768px) {
            .registration-card {
              grid-template-columns: 1fr !important;
              text-align: center;
              gap: 20px !important;
            }
            
            .stats-grid {
              grid-template-columns: repeat(2, 1fr) !important;
              gap: 15px !important;
            }
            
            .action-buttons {
              flex-direction: row !important;
              justify-content: center;
              gap: 8px !important;
            }
            
            .action-buttons button {
              flex: 1;
              font-size: 0.8rem !important;
              padding: 10px 16px !important;
            }
            
            .header-section {
              flex-direction: column !important;
              gap: 15px !important;
              text-align: center;
            }
            
            .header-buttons {
              flex-direction: column !important;
              width: 100% !important;
            }
            
            .header-buttons button {
              width: 100% !important;
            }
            
            h1 {
              font-size: 2rem !important;
            }
            
            .modal-content-responsive {
              margin: 10px !important;
              width: calc(100% - 20px) !important;
            }
            
            .image-actions {
              flex-direction: column !important;
              gap: 8px !important;
            }
            
            .image-actions button {
              width: 100% !important;
            }
          }
          
          @media (max-width: 480px) {
            .stats-grid {
              grid-template-columns: 1fr !important;
            }
            
            .registration-card {
              padding: 20px !important;
            }
            
            h1 {
              font-size: 1.8rem !important;
            }
            
            .action-buttons button {
              font-size: 0.75rem !important;
              padding: 8px 12px !important;
            }
          }
          
          /* تحسين التركيز للوصولية */
          button:focus,
          .focusable:focus {
            outline: 3px solid rgba(195, 20, 50, 0.5);
            outline-offset: 2px;
          }
          
          /* تحسين الطباعة */
          @media print {
            .no-print {
              display: none !important;
            }
            
            .registration-card {
              break-inside: avoid;
              margin-bottom: 20px;
            }
            
            body {
              background: white !important;
            }
            
            .registration-card {
              border: 2px solid #333 !important;
              background: white !important;
            }
          }
          
          /* تحسين الأداء */
          .gpu-accelerated {
            transform: translateZ(0);
            will-change: transform;
          }
          
          /* تحسين التمرير السلس */
          html {
            scroll-behavior: smooth;
          }
          
          /* تحسين التباين للرؤية */
          @media (prefers-contrast: high) {
            .registration-card {
              border-width: 4px !important;
            }
            
            button {
              border: 2px solid currentColor !important;
            }
          }
          
          /* تحسين للحركة المنخفضة */
          @media (prefers-reduced-motion: reduce) {
            * {
              animation-duration: 0.01ms !important;
              animation-iteration-count: 1 !important;
              transition-duration: 0.01ms !important;
            }
          }
        `}
      </style>
    </>
  );
}

export default AdminApprovalPanel;
